INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (248880, 'Deflazacort', 'Azilsartan medoxomil', 'Moderate', 'Corticosteroids may antagonize the effects of antihypertensive medications by inducing sodium and fluid retention.  These effects may be more common with the natural corticosteroids (cortisone, hydrocortisone) because they have greater mineralocorticoid activity.  Conversely, some calcium channel blockers such as diltiazem and verapamil may increase corticosteroid plasma levels and effects by inhibiting their clearance via CYP450 3A4 metabolism.', NULL, 'Patients on prolonged (i.', NULL, 'Patients on prolonged (i.e., longer than about a week) or high-dose corticosteroid therapy should have blood pressure, electrolyte levels, and body weight monitored regularly, and be observed for the development of edema and congestive heart failure.  The dosages of antihypertensive medications may require adjustment.', 'Metabolism, Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/294162/', '[1] "Multum Information Services, Inc. Expert Review Panel"[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (248881, 'Desipramine', 'Azilsartan medoxomil', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation.  Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.  Some authorities recommend avoiding alcohol in patients receiving vasodilating antihypertensive drugs.  Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/294163/', '[1] Sternbach H "Fluoxetine-associated potentiation of calcium-channel blockers." J Clin Psychopharmacol 11 (1991):  390-1[2] Feder R "Bradycardia and syncope induced by fluoxetine." J Clin Psychiatry 52 (1991):  139[3] Ellison JM, Milofsky JE, Ely E "Fluoxetine-induced bradycardia and syncope in two patients." J Clin Psychiatry 51 (1990):  385-6[4] Rodriguez de la Torre B, Dreher J, Malevany I, et al. "Serum levels and cardiovascular effects of tricyclic antidepressants and selective serotonin reuptake inh