INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249004, 'Azilsartan medoxomil', 'Pregabalin', 'Moderate', 'Pregabalin has been associated with the development of angioedema, and coadministration with other drugs that are also known to cause angioedema (e.g., ACE inhibitors, angiotensin II receptor antagonists, renin inhibitors) may theoretically increase the risk.', NULL, 'Clinicians and patients should be aware of the potential for increased risk of angioedema when pregabalin is prescribed with other drugs that are also known to cause angioedema.', NULL, 'Clinicians and patients should be aware of the potential for increased risk of angioedema when pregabalin is prescribed with other drugs that are also known to cause angioedema.  Patients should be advised to promptly discontinue these medications and seek medical attention if they develop signs or symptoms suggestive of angioedema such as swelling of the face, extremities, eyes, lips, or tongue, and difficulty swallowing or breathing.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/294288/', '[1] "Product Information. Lyrica (pregabalin)." Pfizer U.S. Pharmaceuticals Group  (2005):[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249005, 'Azilsartan medoxomil', 'Procarbazine', 'Moderate', 'Monoamine oxidase inhibitors (MAOIs) may potentiate the hypotensive effect of some medications.  MAOIs alone quite commonly produce orthostatic hypotension.  This effect may stem from a gradual MAOI-induced accumulation of false neurotransmitters in peripheral adrenergic neurons that have minimal activity at alpha- and beta-adrenergic receptors, resulting in a functional block of sympathetic neurotransmission.  The interaction has been reported with the concomitant use of beta-blockers.', NULL, 'Caution is advised during coadministration of MAOIs and other medications with hypotensive effects, especially during the first few weeks of treatment.', NULL, 'Caution is advised during coadministration of MAOIs and other medications with hypotensive effects, especially during the first few weeks of treatment.  Close monitoring for development of hypotension is recommended.  Ambulatory patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/294289/', '[1] Reggev A, Vollhardt BR "Bradycardia induced by an interaction between phenelzine and beta blockers." Psychosomatics 30 (1992):  106-8[2] Pettinger WA, Soyangco FG, Oates JA "Inhibition of monoamine oxidase in man by furazolidone." Clin Pharmacol Ther 9 (1968):  442-7[3] Schulz R, Antonin KH, Hoffmann E, et al. "Tyramine kinetics and pressor sensitivity during monoamine oxidase inhibition by