INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249014, 'Ramipril', 'Azilsartan medoxomil', 'Major', 'Coadministration of an ACE inhibitor in combination with an angiotensin II receptor antagonist may increase the risk of hyperkalemia, hypotension, syncope, and renal dysfunction due to additive or synergistic effects on the renin-angiotensin system.', NULL, 'Dual blockade of the renin-angiotensin-aldosterone system by adding an ACE inhibitor to an angiotensin II receptor antagonist is not recommended, especially in patients with diabetic nephropathy.', NULL, 'Dual blockade of the renin-angiotensin-aldosterone system by adding an ACE inhibitor to an angiotensin II receptor antagonist is not recommended, especially in patients with diabetic nephropathy.  Most patients receiving the combination do not obtain any additional benefit compared to monotherapy.  However, if the combination is considered medically necessary, serum electrolytes, blood pressure, and renal function should be closely monitored.  Routine monitoring of electrolytes and renal function may be indicated in the elderly or patients with worsening heart failure or a risk for dehydration.  Potassium supplementation should generally be avoided unless it is closely monitored, and patients should be advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as weakness, listlessness, confusion, tingling of the extremities, and irregular heartbeat.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/294298/', '[1] "Product Information. Diovan (valsartan)." Novartis Pharmaceuticals  (2001):[2] "Product Information. Atacand (candesartan)." Astra-Zenec