INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249024, 'Selegiline', 'Azilsartan medoxomil', 'Moderate', 'Monoamine oxidase inhibitors (MAOIs) may potentiate the hypotensive effect of some medications.  MAOIs alone quite commonly produce orthostatic hypotension.  This effect may stem from a gradual MAOI-induced accumulation of false neurotransmitters in peripheral adrenergic neurons that have minimal activity at alpha- and beta-adrenergic receptors, resulting in a functional block of sympathetic neurotransmission.  The interaction has been reported with the concomitant use of beta-blockers.', NULL, 'Caution is advised during coadministration of MAOIs and other medications with hypotensive effects, especially during the first few weeks of treatment.', NULL, 'Caution is advised during coadministration of MAOIs and other medications with hypotensive effects, especially during the first few weeks of treatment.  Close monitoring for development of hypotension is recommended.  Ambulatory patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/294308/', '[1] Reggev A, Vollhardt BR "Bradycardia induced by an interaction between phenelzine and beta blockers." Psychosomatics 30 (1992):  106-8[2] Pettinger WA, Soyangco FG, Oates JA "Inhibition of monoamine oxidase in man by furazolidone." Clin Pharmacol Ther 9 (1968):  442-7[3] Schulz R, Antonin KH, Hoffmann E, et al. "Tyramine kinetics and pressor sensitivity during monoamine oxidase inhibition by selegiline." Clin Pharmacol Ther 46 (1989):  528-36[4] Goldberg LI "Monoamine oxidase inhibitors: adverse reactions and possible mechanisms." JAMA 190 (1964):  456-62[5] Ban TA "Drug interactions with psychoactive drugs." Dis Nerv Syst 36 (1975):  164-6[6] "Product Information. Matulane (procarbazine)." Roche Laboratories  (2001):[7] De Vita VT, Hahn MA, Oliverio VT "Monoamine oxidase inhibition by a new carcinostatic agent, n-isopropyl-a-(2-methylhydrazino)-p-toluamide (MIH). (30590)." Proc Soc Exp Biol Med 120 (1965):  561-5[8] Kronig MH, Roose SP, Walsh BT, Woodring S, Glassman AH "Blood pressure effects of phenelzine." J Clin Psychopharmacol 3 (1983):  307-10[9] Golwyn DH, Sevlie CP "Monoamine oxidase inhibitor hypertensive crisis headache and orthostatic hypotension." J Clin Psychopharmacol 13 (1993):  77-8[10] "Product Information. Nardil (phenelzine)." Parke-Davis  (2001):[11] "Product Information. Parnate (tranylcypromine)." SmithKline Beecham  (2001):[12] "Product Information. Marplan (isocarboxazid)." Roche Laboratories  (2001):[13] "Product Information. Xatral (alfuzosin)." Sanofi-Synthelabo Canada Inc  (2002):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249025, 'Selexipag', 'Azilsartan medoxomil', 'Moderate', 'Coadministration of medicines targeting the prostacyclin pathway (e.g., prostacyclin (PGI2), PGI2 analogs, or selective non-prostanoid prostacyclin IP receptor agonists) with diuretics, antihypertensive agents, or other vasodilators may potentiate the hypotensive effects of these agents.', NULL, 'While therapies that target the prostacyclin pathway have been used in combination with diuretics, antihypertensives, or other vasodilators in the management of pulmonary arterial hypertension, caution is recommended if they must be administered concurrently.', NULL, 'While therapies that target the prostacyclin pathway have been used in combination with diuretics, antihypertensives, or other vasodilators in the management of pulmonary arterial hypertension, caution is recommended if they must be administered concurrently.  If these drugs are used together, it is generally recommended that blood pressure be measured more frequently until a stable blood pressure pattern is observed.  Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their doctor if they experience dizziness, lightheadedness, syncope, orthostatic hypotension, or tachycardia.  Treatment guidelines should be consulted for the optimum management of pulmonary arterial hypertension.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/294309/', '[1] Buchdahl RM, Taylor P, Warner JD "Nebulised ribavirin for adenovirus pneumonia." Lancet 2 (1985):  1070-1[2] "Product Information. Flolan (epoprostenol)." Glaxo Wellcome  (2001):[3] "Product Information. Xatral (alfuzosin)." Sanofi-Synthelabo Canada Inc  (2002):[4] "Product Information. Remodulin (treprostinil)." United Therapeutics Corporation  (2002):[5] "Product Information. Ventavis (iloprost)." Actelion Pharmaceuticals US Inc  (2005):[6] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[7] Cerner Multum, Inc. "Australian Product Information." O 0', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249026, 'Sildenafil', 'Azilsartan medoxomil', 'Moderate', 'Phosphodiesterase-5 (PDE5) inhibitors may potentiate the blood pressure-lowering effect of antihypertensive medications.  These agents inhibit PDE5-mediated degradation of cyclic guanosine monophosphate (cGMP), which in vascular smooth muscles can lead to peripheral vasodilation.', NULL, 'Caution is advised if PDE5 inhibitors are prescribed in combination with antihypertensive agents.', NULL, 'Caution is advised if PDE5 inhibitors are prescribed in combination with antihypertensive agents.  Patients receiving the combination should be advised to avoid rising abruptly from a sitting or recumbent position, especially following treatment initiation or a dosage increase, and to contact their doctor if they experience symptoms of hypotension such as dizziness, lightheadedness, fainting, or tachycardia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/294310/', '[1] "Product Information. Viagra (sildenafil)." Pfizer U.S. Pharmaceuticals  (2001):[2] "Product Information. Levitra (vardenafil)." Bayer  (2003):[3] "Product Information. Stendra (avanafil)." Vivus Inc  (2012):', 1767363028);
