INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249256, 'Sodium phosphate, monobasic', 'Macimorelin', 'Moderate', 'The following interaction applies only to products containing sodium biphosphate that are used for bowel cleansing.  It does not apply to products containing sodium biphosphate that are used for other, non-laxative related purposes.', NULL, 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with drugs that prolong the QT interval.', NULL, 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with drugs that prolong the QT interval.  Monitoring of baseline and posttreatment serum electrolyte levels is recommended, particularly in the elderly.  Patients should be instructed to drink plenty of clear liquids before, during, and after the bowel preparation process.  Consideration should be given to consumption of 36 to 48 fluid ounces of a carbohydrate-electrolyte solution in the six hours before the first dose.  Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/294735/', '[1] Hill AG, Parry BR "Hypokalaemia following bowel cleansing with sodium phosphate." N Z Med J 109 (1996):  347[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Fleet Phospho Soda (sodium acid phophate-sodium phosphate)." Fleet, CB  (2007):[4] "Product Information. Visicol (sodium acid phophate-sodium phosphate)." Salix Pharmaceuticals  (2007):[5] Cerner Multum, Inc. "Australian Product Information." O 0', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249257, 'Sodium phosphate, monobasic', 'Mannitol', 'Major', 'The following interaction applies only to products containing sodium biphosphate that are used for bowel cleansing.  It does not apply to products containing sodium biphosphate that are used for other, non-laxative related purposes.', NULL, 'Caution is advised when bowel-cleansing phosphate preparations are prescribed in patients treated with agents that affect renal function or perfusion, particularly if they are frail or elderly.', NULL, 'Caution is advised when bowel-cleansing phosphate preparations are prescribed in patients treated with agents that affect renal function or perfusion, particularly if they are frail or elderly.  Bowel-cleansing phosphate preparations should not be used in patients who have impaired renal function or perfusion, dehydration, or uncorrected electrolyte abnormalities.  In patients at risk for acute phosphate nephropathy, baseline and postprocedure labs including serum electrolytes, calcium, phosphate, BUN, and creatinine should be performed.  Patients should be advised not to exceed the recommended dosage of their bowel-cleansing preparation and to drink sufficient quantities of clear fluids during before, during, and after bowel cleansing.  Limited data suggest that administration of an electrolyte rehydration solution may attenuate the electrolyte abnormalities and hypovolemia.  Hospitalization and intravenous fluid hydration may be appropriate for frail or elderly patients who may be unable to drink an adequate volume of fluid.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/294737/', '[1] "Product Information. Fleet Phospho Soda (sodium acid phophate-sodium phosphate)." Fleet, CB  (2007):[2] "Product Information. Visicol (sodium acid phophate-sodium phosphate)." Salix Pharmaceuticals  (2007):[3] FDA. Food and Drug Admnistration "Oral sodium phosphate products for bowel cleansing.  http://www.fda.gov/cder/drug/InfoSheets/HCP/OSP_solutionHCP.pdf"   (2007):', 1767363028);
