INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249295, 'Sodium phosphate, monobasic', 'Norfloxacin', 'Moderate', 'The following interactions apply only to products containing sodium biphosphate that are used for bowel cleansing.  They do not apply to products containing sodium biphosphate that are used for other, non-laxative related purposes.', NULL, 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with agents that can lower the seizure threshold and/or prolong the QT interval.', NULL, 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with agents that can lower the seizure threshold and/or prolong the QT interval.  Bowel cleansing preparations should not be used if these patients have impaired renal function or perfusion, dehydration, or uncorrected electrolyte abnormalities.  Baseline and postprocedure labs including serum electrolytes, phosphate, BUN, and creatinine should be considered, particularly in the elderly.  Patients should be advised not to exceed the recommended dosage of their bowel cleansing preparation and to drink sufficient quantities of clear fluids before, during, and after the bowel preparation process.  Administration of an electrolyte rehydration solution may help attenuate the electrolyte abnormalities and hypovolemia.  Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/294777/',