INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249441, 'Bazedoxifene', 'Apalutamide', 'Moderate', 'Coadministration with apalutamide may decrease the plasma concentrations of drugs that are substrates of the metabolic enzymes CYP450 3A4, CYP450 2C19, CYP450 2C9, and uridine diphosphate glucuronosyltransferase (UGT), as well as substrates of the membrane transporters organic anion transporting polypeptide (OATP) 1B1, P-glycoprotein (P-gp), and breast cancer resistance protein (BCRP).', NULL, 'Caution is advised when apalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, CYP450 2C19, CYP450 2C9, UGT, OATP1B1, P-gp and/or BCRP, particularly sensitive substrates or those with a narrow therapeutic range.', NULL, 'Caution is advised when apalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, CYP450 2C19, CYP450 2C9, UGT, OATP1B1, P-gp and/or BCRP, particularly sensitive substrates or those with a narrow therapeutic range.  Substitution for these medications is recommended when possible, or monitor for potential loss of therapeutic efficacy if coadministration is required.  The prescribing information for concomitant medications should be consulted to assess the benefits versus risks of coadministration of a CYP450 inducer like apalutamide and for any dosage adjustments that may be required.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/295237/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Erleada (apalutamide)." Janssen Biotech, Inc.  (2018):', 1767363028);
