INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249629, 'Bictegravir', 'Lesinurad', 'Moderate', 'Coadministration with inducers of CYP450 3A4, particularly those that can also induce uridine diphosphate glucuronosyltransferase (UGT) 1A1, may decrease the plasma concentrations of bictegravir.  According to the product labeling, bictegravir is a substrate of both CYP450 3A4 and UGT1A1; however, the extent to which each enzymatic pathway contributes to the metabolic clearance of bictegravir has not been reported.', NULL, 'The potential for diminished pharmacologic effects of bictegravir should be considered during coadministration with CYP450 3A4 inducers.', NULL, 'The potential for diminished pharmacologic effects of bictegravir should be considered during coadministration with CYP450 3A4 inducers.  Alternative treatments may be required if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/295482/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Biktarvy (bictegravir/emtricitabine/tenofovir)." Gilead Sciences  (2018):', 1767365210);
