INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249720, 'Bictegravir', 'Ulipristal', 'Moderate', 'Based on in vitro inhibition data, coadministration with ulipristal may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) transporter.  The proposed mechanism is decreased clearance in the intestine, liver, and/or kidney due to inhibition of P-glycoprotein-mediated drug efflux by ulipristal.', NULL, 'Caution is advised when ulipristal is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when ulipristal is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever ulipristal is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/295599/', '[1] "Product Information. Ella (ulipristal)." Afaxys Inc.  (2022):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249721, 'Bictegravir', 'Valbenazine', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) may increase the plasma concentrations of bictegravir.  According to the product labeling, bictegravir is a substrate of both P-gp and BCRP; however, the extent to which each enzymatic pathway contributes to the metabolic clearance of bictegravir has not been reported.', NULL, 'The use of bictegravir with P-gp and/or BCRP inhibitors should generally be avoided.', NULL, 'The use of bictegravir with P-gp and/or BCRP inhibitors should generally be avoided.  If concomitant use is required, caution and close clinical and laboratory monitoring, including renal function, are recommended.  Dosage adjustments may be required whenever a P-gp and/or BCRP inhibitor is added to or withdrawn from therapy.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/295600/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249722, 'Bictegravir', 'Vandetanib', 'Moderate', 'Coadministration with vandetanib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein transporter, such as digoxin and dabigatran.  The mechanism involves increased absorption and/or decreased clearance due to inhibition of drug efflux mediated by intestinal and renal/hepatic P-glycoprotein, respectively.  Vandetanib is a weak P-glycoprotein inhibitor.', NULL, 'Caution is advised if vandetanib must be used concomitantly with medications that are substrates of P-glycoprotein, particularly those with a narrow therapeutic range such as digoxin.', NULL, 'Caution is advised if vandetanib must be used concomitantly with medications that are substrates of P-glycoprotein, particularly those with a narrow therapeutic range such as digoxin.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever vandetanib is added to or withdrawn from therapy.', 'Absorption, Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/295601/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports.  http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid"   (2007):[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Vandetanib (vandetanib)." Astra-Zeneca Pharmaceuticals  (2011):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249723, 'Bictegravir', 'Varenicline', 'Moderate', 'Coadministration with bictegravir may increase the plasma concentrations of drugs that are substrates of organic cation transporter 2 (OCT2) and multidrug and toxin extrusion transporter 1 (MATE1).  The mechanism is inhibition of OCT2- and MATE1-mediated renal tubular secretion by bictegravir.', NULL, 'Caution is advised when bictegravir is used with drugs that are substrates of OCT2 or MATE1, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when bictegravir is used with drugs that are substrates of OCT2 or MATE1, particularly those with a narrow therapeutic range.  Close monitoring is particularly recommended in patients with moderate renal impairment (CrCl 30 to 59 mL/min) who are to be administered bictegravir and metformin, due to the increased risk of lactic acidosis.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever bictegravir is added to or withdrawn from therapy.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/295602/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Biktarvy (bictegravir/emtricitabine/tenofovir)." Gilead Sciences  (2018):', 1767365210);
