INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249772, 'Telotristat ethyl', 'Progesterone', 'Moderate', 'Coadministration with telotristat ethyl may decrease the plasma concentrations of drugs that are substrates of CYP450 3A4 and/or 2B6 isoenzymes.  The proposed mechanism is accelerated clearance due to induction of CYP450 3A4 and/or 2B6 (in vitro) isoenzymes by telotristat ethyl.', NULL, 'When drugs that are known substrates of CYP450 3A4 and/or 2B6 are coadministered with telotristat ethyl, the possibility of a diminished therapeutic response to those drugs should be considered.', NULL, 'When drugs that are known substrates of CYP450 3A4 and/or 2B6 are coadministered with telotristat ethyl, the possibility of a diminished therapeutic response to those drugs should be considered.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs, particularly those with a narrow therapeutic range, whenever telotristat ethyl is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/295891/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Xermelo (telotristat ethyl)." Lexicon Pharmaceuticals, Inc.  (2017):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249773, 'Brentuximab vedotin', 'Metronidazole', 'Moderate', 'Brentuximab vedotin can cause peripheral neuropathy, and concurrent use of other agents that are also associated with this adverse effect can potentiate the risk.', NULL, 'The potential for increased risk and/or severity of peripheral neuropathy should be considered when brentuximab vedotin is used with other drugs that are also associated with peripheral neuropathy.', NULL, 'The potential for increased risk and/or severity of peripheral neuropathy should be considered when brentuximab vedotin is used with other drugs that are also associated with peripheral neuropathy.  Patients should be closely monitored for symptoms of neuropathy such as burning, tingling, pain, numbness, or weakness.  Patients experiencing new, worsening, or recurrent neuropathy may require a delay, change in dosage, or discontinuation of brentuximab vedotin in accordance with the product labeling.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/295893/', '[1] Carrion C, Espinosa E, Herrero A, Garcia B "Possible vincristine-isoniazid interaction." Ann Pharmacother 29 (1995):  201[2] Argov Z, Mastaglia FL "Drug-induced peripheral neuropathies." Br Med J 1 (1979):  663-6[3] Pharmaceutical Society of Australia "APPGuide online. Australian prescription products guide online.  http://www.appco.com.au/appguide/default.asp"   (2006):[4] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports.  http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid"   (2007):[5] "Product Information. Adcetris (brentuximab vedotin)." Seattle Genetics Inc  (2011):[6] "Product Information. Danyelza (naxitamab)." Y-mAbs Therapeutics  (2020):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249774, 'Isosorbide', 'Tetracycline', 'Minor', 'The coadministration of diuretics and tetracyclines may result in decreased renal function manifested by increases in serum creatinine and blood urea nitrogen (BUN).', NULL, 'Usually, no clinical intervention is necessary, unless decreases in renal function occur.', NULL, 'Usually, no clinical intervention is necessary, unless decreases in renal function occur.  If renal function deteriorates, discontinuation of one or both agents may be necessary.', 'Synergism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/295895/', '[1] Jick H, Slone D, Shapiro S, et al. "Tetracycline and drug-attributed rises in blood urea nitrogen: a report from the Boston Collaborative Drug Surveillance Program." JAMA 220 (1972):  377-9[2] Garty M, Hurwitz A "Effect of cimetidine and antacids on gastrointestinal absorption of tetracycline." Clin Pharmacol Ther 28 (1980):  203-7[3] Tannenberg AM "Tetracycline and rises in urea nitrogen." JAMA 221 (1972):  713[4] Alexander MR "Tetracycline and rises in urea nitrogen." JAMA 221 (1972):  713-4', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249775, 'Tetracycline', 'Ketoconazole', 'Major', 'Use of hepatotoxic drugs together with ketoconazole or levoketoconazole may potentiate the risk of liver injury.', NULL, 'The use of ketoconazole or levoketoconazole with other potentially hepatotoxic agents should be avoided.', NULL, 'The use of ketoconazole or levoketoconazole with other potentially hepatotoxic agents should be avoided.  Patients treated with levoketoconazole or ketoconazole should have liver tests performed prior to and during treatment.  Levoketoconazole manufacturer recommends interrupting treatment immediately if signs of hepatotoxicity occur.  Refer to levoketoconazole labeling for specific instructions on management of hepatotoxicity.  Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/295896/', '[1] "Product Information. Ketoconazole (ketoconazole)." Mylan Pharmaceuticals Inc  (2019):[2] "Product Information. Recorlev (levoketoconazole)." Xeris Pharmaceuticals Inc  (2022):[3] Auchus R, Pivonello R, Fleseriu M, et al. "Levoketoconazole: a novel treatment for endogenous Cushing''s syndrome.  https://www.tandfonline.com/doi/pdf/10.1080/17446651.2021.1945440"   (2022):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249776, 'Amobarbital', 'Bisoprolol', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation.  Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.  Some authorities recommend avoiding alcohol in patients receiving vasodilating antihypertensive drugs.  Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/295899/', '[1] Sternbach H "Fluoxetine-associated potentiation of calcium-channel blockers." J Clin Psychopharmacol 11 (1991):  390-1[2] Feder R "Bradycardia and syncope induced by fluoxetine." J Clin Psychiatry 52 (1991):  139[3] Ellison JM, Milofsky JE, Ely E "Fluoxetine-induced bradycardia and syncope in two patients." J Clin Psychiatry 51 (1990):  385-6[4] Rodriguez de la Torre B, Dreher J, Malevany I, et al. "Serum levels and cardiovascular effects of tricyclic antidepressants and selective serotonin reuptake inhibitors in depressed patients." Ther Drug Monit 23 (2001):  435-40[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] Pacher P, Kecskemeti V "Cardiovascular side effects of new antidepressants and antipsychotics: new drugs, old concerns?" Curr Pharm Des 10 (2004):  2463-75[7] Andrews C, Pinner G "Postural hypotension induced by paroxetine." BMJ 316 (1998):  595', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249777, 'Gadoxetic acid', 'Bisoprolol', 'Moderate', 'Limited data suggest that patients receiving beta-blockers may have an increased risk of severe hypotensive and/or hypersensitivity reactions to parenteral contrast media.  In addition, allergic/anaphylactoid reactions in these patients may be more difficult to treat.  The mechanism is unknown.  Theoretically, beta-blocker ophthalmic solutions may also interact, as they are systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels.', NULL, 'Patients who have received beta-blockers should be closely monitored for adverse reactions to contrast media.', NULL, 'Patients who have received beta-blockers should be closely monitored for adverse reactions to contrast media.  If anaphylaxis occurs, clinicians should be aware that beta-blockers may attenuate the response to epinephrine.  Thus, larger doses of epinephrine may be necessary to overcome the bronchospasm, although such large doses can also cause excessive alpha-adrenergic stimulation resulting in hypertension, reflex bradycardia, heart block, and possible potentiation of bronchospasm.  Alternative treatments recommended include vigorous supportive care (e.g., fluids) and the use of parenteral beta-agonists for bronchospasm and norepinephrine for hypotension.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/295900/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Canadian Pharmacists Association "e-CPS.  http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink"   (2006):[3] EMEA