INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249812, 'Carfilzomib', 'Norelgestromin', 'Major', 'Venous thromboembolic events, including deep vein thrombosis (DVT) and pulmonary embolism (PE), have occurred during treatment with carfilzomib, and concurrent use of hormonal contraception also associated with this adverse effect can potentiate the risk.', NULL, 'Hormonal contraception associated with a risk of thrombosis should generally be avoided during use of carfilzomib.', NULL, 'Hormonal contraception associated with a risk of thrombosis should generally be avoided during use of carfilzomib.  Advise female and male patients of reproductive potential to use effective contraception or abstain from sexual activity during treatment with carfilzomib for at least 30 and 90 days, respectively, following completion of therapy.  Advise the patient to contact their physician immediately if pregnancy does occur during these times.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/295947/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Faculty of Sexual & Reproductive Healthcare "FSRH Clinical Guidance: Drug Interactions with Hormonal Contraception.  file:///C:/Users/df033684/Downloads/ceuguidancedruginteractionshormonal.pdf"   (2016):', 1767365210);
