INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249827, 'Dasatinib', 'Norelgestromin', 'Moderate', 'Coadministration with dasatinib may increase the plasma concentrations of drugs that are substrates of CYP450 3A4.  The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by dasatinib, which is a time-dependent inhibitor of the isoenzyme.', NULL, 'Caution is advised if dasatinib must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if dasatinib must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever dasatinib is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/295962/', '[1] "Product Information. Sprycel (dasatinib)." Bristol-Myers Squibb  (2006):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249828, 'Deferasirox', 'Norelgestromin', 'Moderate', 'Coadministration with deferasirox may decrease the plasma concentrations of contraceptive hormones.  The proposed mechanism is deferasirox induction of CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of sex hormones.', NULL, 'Hormonal contraceptives agents may not be reliable when used with deferasirox.', NULL, 'Hormonal contraceptives agents may not be reliable when used with deferasirox.  Female patients of reproductive potential receiving hormonal contraceptives should be counseled to use alternative or additional methods of birth control during treatment with deferasirox.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/295963/', '[1] "Product Information. Desferal (deferoxamine)." Novartis Pharmaceuticals  (2001):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249829, 'Valproic acid', 'Norelgestromin', 'Moderate', 'A case report suggests that estrogens or progestins may decrease the serum concentrations and pharmacologic effects of valproic acid (VPA).  The proposed mechanism is induction of hepatic glucuronidation by sex hormones.', NULL, 'Pharmacologic response and serum valproic levels should be monitored more closely whenever estrogen- and/or progestin-containing drugs are added to or withdrawn from therapy, and the valproic acid dosage adjusted as necessary.', NULL, 'Pharmacologic response and serum valproic levels should be monitored more closely whenever estrogen- and/or progestin-containing drugs are added to or withdrawn from therapy, and the valproic acid dosage adjusted as necessary.  Patients should be advised to contact their physician if they experience loss of seizure control or symptoms of valproic acid toxicity such as tremors, ataxia, nystagmus, increased seizures, and changes in mental status.  In patients receiving oral contraceptives, gradual transient increases in valproic acid levels will likely occur during the pill-free week for women not also taking an enzyme-inducing drug (e.g., carbamazepine, phenytoin, phenobarbital, primidone, rifampin).  The increase in valproic acid levels will be greater if the dose of valproic acid is increased in the few days before or during the pill-free week.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/295964/', '[1] "Product Information. Depakene (valproic acid)." Abbott Pharmaceutical  (2001):[2] "Product Information. Depakote (divalproex sodium)." Abbott Pharmaceutical  (2001):[3] Crawford P, Chadwick D, Cleland P, Tjia J, Cowie A, Back DJ, Orme ML "The lack of effect of sodium valproate on the pharmacokinetics of oral contraceptive steroids." Contraception 33 (1986):  23-9[4] "Product Information. Depacon (valproic acid)." Abbott Pharmaceutical  (2001):[5] Herzog AG, Farina EL, Blum AS "Serum valproate levels with oral contraceptive use." Epilepsia 46 (2005):  970-1', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249830, 'Dronedarone', 'Norelgestromin', 'Moderate', 'Coadministration with dronedarone may increase the plasma concentrations of drugs that are substrates of the CYP450 2D6 isoenzyme, CYP450 3A4 isoenzyme, and/or P-glycoprotein (P-gp) efflux transporter.  The mechanism is decreased clearance via these routes due to inhibition by dronedarone.', NULL, 'Caution is advised when dronedarone is used concomitantly with drugs that are substrates of CYP450 2D6, CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when dronedarone is used concomitantly with drugs that are substrates of CYP450 2D6, CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever dronedarone is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/295965/', '[1] "Product Information. Multaq (dronedarone)." sanofi-aventis  (2009):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249831, 'Duvelisib', 'Norelgestromin', 'Moderate', 'Coadministration with duvelisib may increase the plasma concentrations of drugs that are primarily metabolized by the CYP450 3A4 isoenzyme.  The mechanism is reduced clearance due to inhibition of CYP450 3A4 by duvelisib.', NULL, 'Caution is advised if duvelisib is used in combination with sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index (e.', NULL, 'Caution is advised if duvelisib is used in combination with sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index (e.g., ergot alkaloids, colchicine, fentanyl, macrolide immunosuppressants, midazolam, triazolam, vinca alkaloids).  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever duvelisib is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/295966/', '[1] "Product Information. Copiktra (duvelisib)." Verastem, Inc.  (2018):[2] "Product Information. Copiktra (duvelisib)." Verastem, Inc.  (2021):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249832, 'Echinacea', 'Norelgestromin', 'Moderate', 'Coadministration with echinacea may alter the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 3A4.  Echinacea appears to inhibit intestinal CYP450 3A4, which would lead to an increase in oral midazolam (a sensitive 3A4 substrate) bioavailability; however, plasma levels of midazolam following oral administration do not appear to be affected by echinacea.  In contrast, it appears that echinacea may also induce hepatic CYP450 3A4; thereby increasing the hepatic clearance of drugs that are substrates of CYP450 3A4.', NULL, 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines.', NULL, 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines.  If echinacea is prescribed with a drug that is a CYP450 3A4 substrate, the possibility of an altered (increased or decreased) therapeutic response should be considered.  Patients should be monitored more closely following the addition or withdrawal of echinacea and the dosage of the CYP450 3A4 substrate adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/295967/', '[1] Gorski JC, Huang SM, Pinto A, et al. "The effect of echinacea (Echinacea purpurea root) on cytochrome P450 activity in vivo." Clin Pharmacol Ther 75 (2004):  89-100[2] Cerner Multum, Inc. "Australian Product Information." O 0', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249833, 'Efavirenz', 'Norelgestromin', 'Moderate', 'There are limited data concerning the potential effect of efavirenz on hormonal contraceptives.  Presumably, coadministration with efavirenz may decrease the efficacy of contraceptives containing low-dose estrogens and progestins due to its induction of CYP450 3A4, the isoenzyme that is primarily responsible for the metabolic clearance of sex hormones.', NULL, 'Because the potential interaction between efavirenz and contraceptive hormones has not been fully characterized, and use of efavirenz has been associated with fetal malformations in animal studies, the manufacturer recommends that women avoid pregnancy during efavirenz treatment and that a barrier form of contraception be used in addition to hormonal contraceptives.', NULL, 'Because the potential interaction between efavirenz and contraceptive hormones has not been fully characterized, and use of efavirenz has been associated with fetal malformations in animal studies, the manufacturer recommends that women avoid pregnancy during efavirenz treatment and that a barrier form of contraception be used in addition to hormonal contraceptives.  Alternative or additional methods of birth control should be used during and for at least 4 weeks after efavirenz therapy.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.  Intrauterine systems are unlikely to be significantly affected because of their local action.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/295968/', '[1] "Product Information. Sustiva (efavirenz)." DuPont Pharmaceuticals  (2001):[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Lakhi N, Govind A "Implanon failure in patients on antiretroviral medication: the importance of disclosure." J Fam Plann Reprod Health Care 36 (2010):  181-2[4] Faculty of Sexual & Reproductive Healthcare "FSRH Clinical Guidance: Drug Interactions with Hormonal Contraception.  file:///C:/Users/df033684/Downloads/ceuguidancedruginteractionshormonal.pdf"   (2016):', 1767365210);
