INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249835, 'Elvitegravir', 'Norelgestromin', 'Moderate', 'Coadministration with cobicistat and elvitegravir may alter the plasma concentrations of contraceptive hormones.  The exact mechanism of interaction has not been described.', NULL, 'The potential risks and benefits of using norgestimate-containing contraceptives in combination with cobicistat and elvitegravir should be considered, particularly in women who have risk factors for insulin resistance, dyslipidemia, acne, and venous thrombosis.', NULL, 'The potential risks and benefits of using norgestimate-containing contraceptives in combination with cobicistat and elvitegravir should be considered, particularly in women who have risk factors for insulin resistance, dyslipidemia, acne, and venous thrombosis.  Coadministration of cobicistat and elvitegravir with other hormonal contraceptives (e.g., contraceptive patch, contraceptive vaginal ring, or injectable contraceptives), oral contraceptives containing progestogens other than norgestimate, or less than 25 mcg of ethinyl estradiol has not been studied and is not recommended.  Some authorities recommend that hormonal contraceptives containing at least 30 mcg of ethinyl estradiol with norgestimate should be used in combination with the multi-ingredient antiretroviral formulations containing elvitegravir/cobicistat/emtricitabine/tenofovir disoproxil fumarate or elvitegravir/cobicistat/emtricitabine/tenofovir alafenamide.  However, the long term effects of increased progesterone exposure are not known.  Alternative, nonhormonal methods of contraception may be considered.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/295970/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Stribild (cobicistat/elvitegravir/emtricitabine/tenofov)." Gilead Sciences  (2012):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249836, 'Emapalumab', 'Norelgestromin', 'Moderate', 'Plasma concentrations of drugs that are CYP450 substrates may decrease following the initiation of interleukin (IL) inhibitors, tumor necrosis factor (TNF) blockers, or interferon (IFN) inhibitors in patients with chronic inflammatory diseases.  Because the formation of hepatic CYP450 enzymes is down-regulated during infection and chronic inflammation by increased levels of certain cytokines (e.g., interleukins-1, -6, and -10; tumor necrosis factor alpha; interferons), treatment targeting these cytokines may restore or normalize CYP450 enzyme levels resulting in increased metabolism of these drugs.', NULL, 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.', NULL, 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.g., antiarrhythmics, anticonvulsants, immunosuppressants, theophylline) or sensitive substrates where decreases in plasma levels may be significant or undesirable (e.g., oral contraceptives, statins, benzodiazepines, opioids).  Clinical and/or laboratory monitoring should be considered following the initiation or withdrawal of such treatments, and the dosage(s) of the CYP450 substrate(s) adjusted accordingly.  Clinicians should note that the effects of IL inhibitors, TNF blockers, and IFN inhibitors on CYP450 activities may persist for several weeks after stopping therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/295971/', '[1] "Product Information. Remicade (infliximab)." Centocor Inc  (2001):[2] "Product Information. Amevive (alefacept)." Biogen  (2003):[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Arcalyst (rilonacept)." Regeneron Pharmaceuticals Inc  (2008):[5] "Product Information. Stelara (ustekinumab)." Centocor Inc  (2009):[6] "Product Information. Simponi (golimumab)." Centocor Inc  (2009):[7] "Product Information. Ilaris (canakinumab)." Novartis Pharmaceuticals  (2009):[8] "Product Information. Actemra (tocilizumab)." Genentech  (2010):[9] "Product Information. Sylvant (siltuximab)." Janssen Biotech, Inc.  (2014):[10] "Product Information. Cosentyx (secukinumab)." Novartis Pharmaceuticals  (2015):[11] "Product Information. Taltz Autoinjector (ixekizumab)." Eli Lilly and Company  (2016):[12] "Product Information. Kevzara (sarilumab)." sanofi-aventis  (2017):[13] "Product Information. Ilumya (tildrakizumab)." Merck & Co., Inc  (2018):[14] "Product Information. Gamifant (emapalumab)." Sobi Inc  (2018):[15] "Product Information. Skyrizi (risankizumab)." AbbVie US LLC  (2019):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249837, 'Enasidenib', 'Norelgestromin', 'Moderate', 'Coadministration with enasidenib may alter the plasma concentrations of hormonal contraceptives.  In vitro, enasidenib has been found to both inhibit and induce CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of contraceptive hormones.', NULL, 'Because use of enasidenib may be associated with fetal harm, it is important that patients not become pregnant during treatment.', NULL, 'Because use of enasidenib may be associated with fetal harm, it is important that patients not become pregnant during treatment.  It may be advisable to monitor patients for potentially altered effects of their hormonal contraceptive during and after treatment with enasidenib.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.  Intrauterine systems are unlikely to be significantly affected because of their local action.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/295972/', '[1] "Product Information. Idhifa (enasidenib)." Celgene Corporation  (2017):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249838, 'Encorafenib', 'Norelgestromin', 'Major', 'Coadministration with encorafenib may decrease the plasma concentrations and efficacy of contraceptive hormones.  The proposed mechanism is induction of CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of sex hormones.', NULL, 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, may not be reliable during concomitant therapy with encorafenib.', NULL, 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, may not be reliable during concomitant therapy with encorafenib.  Because encorafenib can cause fetal harm, it is particularly important that patients not become pregnant during treatment.  Therefore, females of reproductive potential should use an effective, nonhormonal method of contraception during treatment and for 2 weeks after the final dose of encorafenib.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.  Intrauterine systems are unlikely to be significantly affected because of their local action.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/295973/', '[1] "Product Information. Braftovi (encorafenib)." Array BioPharma Inc.  (2018):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249839, 'Entrectinib', 'Norelgestromin', 'Moderate', 'Coadministration with entrectinib may render systemic hormonal contraceptives ineffective.  The mechanism for the potential interaction has not been established and there are no clinical data regarding the use of entrectinib with hormonal contraceptives.', NULL, 'Until further data are available, females of reproductive potential should be advised to use effective non-hormonal contraception during treatment and for 5 weeks after the final dose.', NULL, 'Until further data are available, females of reproductive potential should be advised to use effective non-hormonal contraception during treatment and for 5 weeks after the final dose.  In addition, men receiving entrectinib should use highly effective contraceptive methods during treatment and for 3 months after the final dose.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/295974/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Rozlytrek (entrectinib)." Genentech  (2019):', 1767365210);
