INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249845, 'Fedratinib', 'Norelgestromin', 'Moderate', 'Coadministration with fedratinib may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4, 2C19, and/or 2D6 isoenzymes.', NULL, 'Caution is recommended when fedratinib is used concomitantly with substrates of CYP450 3A4, 2C19, and/or 2D6.', NULL, 'Caution is recommended when fedratinib is used concomitantly with substrates of CYP450 3A4, 2C19, and/or 2D6.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever fedratinib is added to or withdrawn from therapy, particularly those with a narrow therapeutic index.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/295980/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Inrebic (fedratinib)." Celgene Corporation  (2019):', 1767365210);
