INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249891, 'Oxtriphylline', 'Norelgestromin', 'Moderate', 'The coadministration with contraceptive steroids may increase the plasma concentrations of theophylline and other methylxanthines.  The proposed mechanism is inhibition of methylxanthine metabolism via hepatic microsomal enzymes, of which estrogens and progestins are also substrates.', NULL, 'Caution is advised during concomitant therapy with methylxanthines and hormonal contraceptives.', NULL, 'Caution is advised during concomitant therapy with methylxanthines and hormonal contraceptives.  Serum theophylline levels and pharmacologic response should be monitored and the dosage adjusted accordingly, particularly following initiation or discontinuation of hormonal contraceptives in patients who are stabilized on their theophylline regimen.  Patients should be advised to notify their physician if they experience signs and symptoms of theophylline toxicity such as nausea, vomiting, diarrhea, headache, restlessness, insomnia, and irregular heartbeat.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/296034/', '[1] Tornatore KM, Kanarkowski R, McCarthy TL, et al. "Effect of chronic oral contraceptive steroids on theophylline disposition." Eur J Clin Pharmacol 23 (1982):  129-34[2] Gardner MJ, Tornatore KM, Jusko WJ, Kanarkowski R "Effects of tobacco smoking and oral contraceptive use on theophylline disposition." Br J Clin Pharmacol 16 (1983):  271-80[3] Roberts RK, Grice J, McGuffie, Heilbronn L "Oral contraceptive steroids impair the elimination of theophylline." J Lab Clin Med 101 (1983):  821-25', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249892, 'Pentobarbital', 'Norelgestromin', 'Moderate', 'Coadministration with a barbiturate may reduce the efficacy of contraceptive hormones.The interaction stems from accelerated clearance of contraceptive hormones as well as decreased plasma concentrations of unbound (active) hormones due to induction of hepatic CYP450 enzymatic activity and enhancement of hormone-binding globulin capacity by phenobarbital.  Since all barbiturates are believed to possess enzyme-inducing capabilities, the interaction should be expected with agents in the class other than phenobarbital.', NULL, 'Women using hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with barbiturates.', NULL, 'Women using hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with barbiturates.  Alternative or additional methods of birth control should be used during and for at least one week after short-term and 4 weeks after long-term (greater than 4 weeks) barbiturate therapy.  No precautions or recommendations are available for women using hormone-releasing intrauterine systems, but a significant interaction with these systems is thought to be unlikely due to their local action.  Injectable progestin-only contraceptives are also thought to be unaffected by barbiturates.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/296036/', '[1] Baci