INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249934, 'Tranexamic acid', 'Norelgestromin', 'Major', 'Because tranexamic acid is an antifibrinolytic agent, concomitant use may further exacerbate the risk of thrombotic events, including venous thromboembolism as well as arterial thromboses such as stroke and myocardial infarction, associated with hormonal contraceptives.', NULL, 'Due to the potential for increased risk of thrombosis in women receiving hormonal contraceptives, the use of tranexamic acid for the treatment of menorrhagia in this population is considered contraindicated.', NULL, 'Due to the potential for increased risk of thrombosis in women receiving hormonal contraceptives, the use of tranexamic acid for the treatment of menorrhagia in this population is considered contraindicated.  Patients receiving hormonal contraceptives should be advised to seek medical attention immediately if they experience potential signs and symptoms of blood clots such as chest pain, shortness of breath, hemoptysis, hematuria, sudden loss of vision, and pain, redness or swelling in an extremity.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/296081/', '[1] "Product Information. Cyklokapron (tranexamic acid)." Pharmacia and Upjohn  (2001):[2] van Hylckama Vlieg A, Helmerhorst FM, Vandenbroucke JP, Doggen CJ, Rosendaal FR "The venous thrombotic risk of oral contraceptives, effects of oestrogen dose and  progestogen type: results of the MEGA case-control study." BMJ 339 (2009):  b2921[3] Lidegaard O, Lokkegaard E, Svendsen AL, Agger C "Hormonal contraception and risk of venous thromboembolism: national follow-up study." BMJ 339 (2009):  b2890[4]