INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249941, 'Voxelotor', 'Norelgestromin', 'Moderate', 'Coadministration with voxelotor may increase the plasma concentrations of drugs that are primarily metabolized by the CYP450 3A4 isoenzyme.  The mechanism is reduced clearance due to inhibition of CYP450 3A4 by voxelotor.  The interaction may be significant for sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index.', NULL, 'Caution is advised when voxelotor is used concomitantly with drugs that undergo metabolism by CYP450 3A4.', NULL, 'Caution is advised when voxelotor is used concomitantly with drugs that undergo metabolism by CYP450 3A4.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever voxelotor is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/296089/', '[1] "Product Information. Oxbryta (voxelotor)." Global Blood Therapeutics, Inc.  (2019):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249942, 'Brentuximab vedotin', 'Rosuvastatin', 'Moderate', 'Brentuximab vedotin can cause peripheral neuropathy, and concurrent use of other agents that are also associated with this adverse effect can potentiate the risk.', NULL, 'The potential for increased risk and/or severity of peripheral neuropathy and hepatotoxicity should be considered when brentuximab vedotin is used with other drugs that are also associated with these adverse effects.', NULL, 'The potential for increased risk and/or severity of peripheral neuropathy and hepatotoxicity should be considered when brentuximab vedotin is used with other drugs that are also associated with these adverse effects.  Patients should be closely monitored for symptoms of neuropathy such as burning, tingling, pain, numbness, or weakness.  Liver enzymes and bilirubin should be measured before and during treatment, especially in patients with underlying hepatic disease or marked baseline transaminase elevations.  Patients experiencing new, worsening, or recurrent neuropathy or hepatotoxicity may require a delay, change in dosage, or discontinuation of brentuximab vedotin in accordance with the product labeling.  Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/296090/', '[1] "Product Information. Adcetris (brentuximab vedotin)." Seattle Genetics Inc  (2011):', 1767365210);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (249943, 'Glycopyrronium (topical)', 'Methscopolamine', 'Moderate', 'The potential exists for additive anticholinergic effects such as mydriasis, blurred vision, heat intolerance, fever, dry mouth, tachycardia, urinary retention, constipation, and glaucoma (onset or exacerbation) when topical or inhaled anticholinergic agents are used with each other or with other agents that possess anticholinergic properties.  The risk of systemic anticholinergic effects following topical administration depends on variables such as strength of the product, size of the application area, frequency of application, and use of occlusive dressing.  Systemic effects are uncommon following oral inhalation or nasal administration due to the poor absorption of quaternary ammonium compounds from gastrointestinal and nasal mucosa.', NULL, 'Topical and inhaled anticholinergic preparations should preferably not be used in combination with other anticholinergic agents or agents with significant anticholinergic effects such as antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, and class IA antiarrhythmics (especially disopyramide).', NULL, 'Topical and inhaled anticholinergic preparations should preferably not be used in combination with other anticholinergic agents or agents with significant anticholinergic effects such as antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, and class IA antiarrhythmics (especially disopyramide).  Caution is advised if concomitant use cannot be avoided, particularly in the elderly and those with significantly impaired renal and/or hepatic function.  Measures should be taken whenever possible to minimize ocular exposure to these drugs, such as keeping eyes closed during oral inhalation, use of a mouthpiece rather than face mask during nebulization, and not touching the eyes following topical application until hands are washed with soap and water.  Patients should be advised to contact their physician if they experience excessive anticholinergic adverse effects or signs and symptoms of angle-closure glaucoma (e.g., eye pain or discomfort; blurred vision; visual halos; colored images in association with red eyes from conjunctival congestion or corneal edema).', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/296093/', '[1] "Product Information. Atrovent (ipratropium)." Boehringer-Ingelheim  (2002):[2] "Product Information. Combivent (albuterol-ipratropium)." Boehringer-Ingelheim  (2001):[3] "Product Information. Spiriva (tiotropium)." Boehringer Ingelheim  (2002):[4] "Product Information. Tudorza Pressair (aclidinium)." Forest Pharmaceuticals  (2012):[5] Cole JM, Sheehan AH, Jordan JK "Concomitant use of ipratropium and tiotropium in chronic obstructive plmonary disease." Ann Pharmacother 46 (2012):  1717-21[6] "Product Information. Anoro Ellipta (umeclidinium-vilanterol)." GlaxoSmithKline  (2014):[7] "Product Information. Qbrexza (glycopyrrolate topical)." Dermira, Inc.  (2018):[8] "Product Information. Yupelri (revefenacin)." Mylan Specialty  (2018):', 1767365210);
