INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1473398, 'Hepatitis A Vaccine', 'Ozanimod', 'Moderate', 'INTERVAL: The administration of inactivated, killed, or otherwise noninfectious vaccines during ozanimod therapy is generally safe but may be associated with a diminished or suboptimal immunologic response. Vaccination may be less effective during and for up to three months after discontinuation of ozanimod therapy.', 'DDInter', 1767317476, 'Immune response to vaccines may be reduced during and for up to three months after discontinuation of ozanimod therapy. In patients who have recently been vaccinated, ozanimod therapy should preferably be postponed for one month to allow the full effect of vaccination to occur.', 'Others', 'Immune response to vaccines may be reduced during and for up to three months after discontinuation of ozanimod therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
