INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1473470, 'Penbutolol', 'Ozanimod', 'Moderate', 'The risk of transient bradycardia and atrioventricular (AV) block may be increased during initiation of ozanimod treatment in patients receiving other drugs that slow heart rate or AV conduction such as beta-blockers, certain calcium channel blockers (e.g., diltiazem, verapamil), and digitalis. Ozanimod can cause a decrease in heart rate during initiation of therapy.', 'DDInter', 1767317476, 'Ozanimod has not been adequately studied in patients receiving concomitant therapy with drugs that decrease heart rate. In addition, advice from a cardiologist should be sought if coadministration of ozanimod is considered in patients with significant QT prolongation (QTcF greater than 450 msec in males or 470 msec in females), patients with arrhythmias requiring treatment with Class 1a or Class III antiarrhythmic agents, patients with ischemic heart disease, heart failure, history of cardiac arrest or myocardial infarction, cerebrovascular disease, or uncontrolled hypertension. Use of ozanimod in patients with a history of second-degree Mobitz type II or higher AV block, sick-sinus syndrome, or sinoatrial heart block is considered contraindicated unless the patient has a functioning pacemaker.', 'Synergism', 'Ozanimod has not been adequately studied in patients receiving concomitant therapy with drugs that decrease heart rate.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1473471, 'Pentobarbital', 'Ozanimod', 'Moderate', 'Limited clinical data suggest that nonselective monoamine oxidase inhibitors (MAOIs) may enhance or prolong the central nervous system effects of barbiturates. The mechanism of interaction is unknown, but may involve inhibition of barbiturate metabolism by MAOIs. Somnolence, dizziness, ataxia, and semi-consciousness have been reported.', 'DDInter', 1767317476, 'Until more information is available, patients should be monitored for increased barbiturate effects during coadministration with an MAOI or other agents that possess MAOI activity such as furazolidone, linezolid, methylene blue, and procarbazine.', 'Metabolism', 'Until more information is available, patients should be monitored for increased barbiturate effects during coadministration with an MAOI or other agents that possess MAOI activity such as furazolidone, linezolid, methylene blue, and procarbazine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
