INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1473500, 'Primidone', 'Ozanimod', 'Major', 'Coadministration of ozanimod with strong CYP450 2C8 inducers may decrease the exposure (AUC) of the major active metabolites of ozanimod which may lead to loss of efficacy. The proposed mechanism is induction of CYP450 2C8-mediated metabolism of ozanimod, which has been shown to be metabolized by this isoenzyme.', 'DDInter', 1767317476, 'Coadministration of ozanimod with strong CYP450 2C8 inducers should be avoided due to the potential for reduced efficacy.', 'Metabolism', 'Coadministration of ozanimod with strong CYP450 2C8 inducers should be avoided due to the potential for reduced efficacy.', NULL, NULL, 'Major', 'pharmacodynamic');
