INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1473502, 'Procainamide', 'Ozanimod', 'Major', 'Class IA (e.g., disopyramide, quinidine, procainamide) and class III (e.g., amiodarone, dofetilide, sotalol) antiarrhythmic agents can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317476, 'Coadministration of class IA or class III antiarrhythmic agents with other drugs that can prolong the QT interval should preferably be avoided unless benefits are anticipated to outweigh the risks. Caution and clinical monitoring are recommended if concomitant use is required. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Coadministration of class IA or class III antiarrhythmic agents with other drugs that can prolong the QT interval should preferably be avoided unless benefits are anticipated to outweigh the risks.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1473503, 'Procarbazine', 'Ozanimod', 'Major', 'Coadministration of ozanimod with monoamine oxidase inhibitors (MAOIs) may increase the risk of hypertensive crisis. The proposed mechanism may involve inhibition of monoamine oxidase by the metabolites of ozanimod. In addition, MAO-B inhibitors may decrease exposure of the active metabolites of ozanimod which may lead to loss of ozanimod efficacy. This interaction has not been studied in a clinical setting.', 'DDInter', 1767317476, 'Coadministration of ozanimod with MAOIs is considered contraindicated. At least 14 days should elapse between discontinuation of ozanimod and initiation of treatment with MAOIs.', 'Synergism', 'Coadministration of ozanimod with MAOIs is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
