INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1473514, 'Racepinephrine', 'Ozanimod', 'Moderate', 'Monoamine oxidase inhibitors (MAOIs) can potentiate the cardiovascular adverse effects of beta-2 adrenergic agonists such as hypertension, palpitation, tachycardia, and chest pain.', 'DDInter', 1767317476, 'Cardiovascular status should be closely monitored when beta-2 agonists are coadministered with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, linezolid, methylene blue, procarbazine). Preferably, at least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with beta-2 agonists.', 'Synergism', 'Cardiovascular status should be closely monitored when beta-2 agonists are coadministered with MAOIs or other agents that possess MAOI activity (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
