INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1473517, 'Ranolazine', 'Ozanimod', 'Moderate', 'Ranolazine can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317476, 'Caution is recommended if ranolazine is used in combination with other drugs that can prolong the QT interval. Since the magnitude of QTc prolongation increases with increasing plasma concentrations of ranolazine, the maximum recommended dosage of 1000 mg twice daily should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. Ranolazine is contraindicated in patients with liver cirrhosis because of the profound effect on QT prolongation in this population.', 'Synergism', 'Caution is recommended if ranolazine is used in combination with other drugs that can prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1473518, 'Rasagiline', 'Ozanimod', 'Major', 'Coadministration of ozanimod with monoamine oxidase inhibitors (MAOIs) may increase the risk of hypertensive crisis. The proposed mechanism may involve inhibition of monoamine oxidase by the metabolites of ozanimod. In addition, MAO-B inhibitors may decrease exposure of the active metabolites of ozanimod which may lead to loss of ozanimod efficacy. This interaction has not been studied in a clinical setting.', 'DDInter', 1767317476, 'Coadministration of ozanimod with MAOIs is considered contraindicated. At least 14 days should elapse between discontinuation of ozanimod and initiation of treatment with MAOIs.', 'Synergism', 'Coadministration of ozanimod with MAOIs is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1473519, 'Regorafenib', 'Ozanimod', 'Major', 'Coadministration with breast cancer resistance protein (BCRP) inhibitors may increase the exposure of the active metabolites of ozanimod and the risk of adverse effects. Coadministration with cyclosporine, a BCRP inhibitor, did not alter the AUC of ozanimod, but it did double the exposure of the minor active metabolites of ozanimod, RP101988 and RP101075 which are direct precursors of the major active metabolite CC112273.', 'DDInter', 1767317476, 'According to the manufacturer, coadministration of ozanimod with BCRP inhibitors is not recommended.', 'Others', 'According to the manufacturer, coadministration of ozanimod with BCRP inhibitors is not recommended.', NULL, NULL, 'Major', 'pharmacodynamic');
