INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1473530, 'Rizatriptan', 'Ozanimod', 'Moderate', 'Coadministration of serotonergic agents (e.g., selective serotonin reuptake inhibitors (SSRIs), 5-HT1 receptor agonists (triptans), ergot alkaloids, buspirone, dextromethorphan, St. John''s wort) with drugs that possess monoamine oxidase inhibition (MAOI) activity may potentiate the risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors. Symptoms of the serotonin syndrome may include mental status changes such as irritability, altered consciousness, confusion, hallucinations, and coma; autonomic dysfunction such as tachycardia, hyperthermia, diaphoresis, shivering, blood pressure lability, and mydriasis; neuromuscular abnormalities such as hyperreflexia, myoclonus, tremor, rigidity, and ataxia; and gastrointestinal symptoms such as abdominal cramping, nausea, vomiting, and diarrhea.', 'DDInter', 1767317476, 'Until more information is available, concomitant use of ozanimod with serotonergic agents should be avoided when possible. Blood pressure and other vitals should be monitored if coadministration is required.', 'Synergism', 'Until more information is available, concomitant use of ozanimod with serotonergic agents should be avoided when possible.', NULL, NULL, 'Moderate', 'pharmacodynamic');
