INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1473678, 'Fostamatinib', 'Palbociclib', 'Moderate', 'Coadministration of fostamatinib with inhibitors of CYP450 3A4 may increase exposure to the active metabolite known as R406, the predominant moiety in the systemic circulation following fostamatinib administration. Fostamatinib is metabolized in the gut by alkaline phosphatase to R406, which then undergoes oxidation via CYP450 3A4 and glucuronidation via UGT1A9. In vitro, R406 is also a substrate of the P-glycoprotein (P-gp) efflux transporter.', 'DDInter', 1767317476, 'Patients should be monitored for toxicities of fostamatinib during concomitant use of potent or moderate CYP450 3A4 inhibitors or dual CYP450 3A4/P-gp inhibitors, and the fostamatinib dosage adjusted as necessary in accordance with the product labeling.', 'Metabolism', 'Patients should be monitored for toxicities of fostamatinib during concomitant use of potent or moderate CYP450 3A4 inhibitors or dual CYP450 3A4/P-gp inhibitors, and the fostamatinib dosage adjusted as necessary in accordance with the product labeling.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1473679, 'Ganciclovir', 'Palbociclib', 'Moderate', 'The use of ganciclovir with other potentially myelotoxic and/or nephrotoxic agents may increase the risk and severity of hematologic toxicity and/or nephrotoxicity due to both additive pharmacodynamic effects and additive effects on the kidney. Ganciclovir alone may cause severe leucopenia, neutropenia, anemia, thrombocytopenia, pancytopenia, bone marrow depression, aplastic anemia, increased serum creatinine levels, and acute renal failure, and the risk may theoretically increase when coadministered with other myelotoxic or nephrotoxic therapy.', 'DDInter', 1767317476, 'Ganciclovir or its prodrug, valganciclovir, should generally not be combined with other drugs associated with myelosuppression and/or nephrotoxicity unless the benefits are anticipated to outweigh the potential risks. Extreme caution is advised if they are used in patients who have recently received or are receiving treatment with myelotoxic and/or nephrotoxic drugs, and vice versa. If coadministration is required, reduced dosages of one or more of the drugs may be required, and the patient should be monitored for the development of hematologic and/or renal adverse effects both during and after discontinuation of therapy.', 'Synergism', 'Ganciclovir or its prodrug, valganciclovir, should generally not be combined with other drugs associated with myelosuppression and/or nephrotoxicity unless the benefits are anticipated to outweigh the potential risks.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1473680, 'Gilteritinib', 'Palbociclib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or P-glycoprotein (P-gp) may increase the plasma concentrations of gilteritinib, which is a substrate of both the isoenzyme and the efflux transporter.', 'DDInter', 1767317476, 'Caution is advised when gilteritinib is used with CYP450 3A4 and/or P-gp inhibitors. Patients should be monitored for adverse effects such as QT interval prolongation, pancreatitis, liver transaminase and bilirubin elevations, edema, infections and stomatitis, and the gilteritinib dosage adjusted as necessary in accordance with the product labeling.', 'Metabolism', 'Caution is advised when gilteritinib is used with CYP450 3A4 and/or P-gp inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1473681, 'Glasdegib', 'Palbociclib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of glasdegib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Caution is advised when glasdegib is used with CYP450 3A4 inhibitors. Patients should be monitored for adverse effects such as QT interval prolongation, anemia, febrile neutropenia, thrombocytopenia, hemorrhage, edema and mucositis, and the glasdegib dosage adjusted as necessary in accordance with the product labeling.', 'Metabolism', 'Caution is advised when glasdegib is used with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1473682, 'Goldenseal', 'Palbociclib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of palbociclib, which is a substrate of the isoenzyme.', 'DDInter', 1767317476, 'Caution is advised when palbociclib is used with CYP450 3A4 inhibitors. Dosage adjustment for palbociclib is not generally considered necessary for suspected interactions with mild to moderate inhibitors. However, patients should be monitored for increased adverse effects such as infections, neutropenia, leucopenia, anemia, thrombocytopenia, anorexia, nausea, vomiting, diarrhea, stomatitis, alopecia, asthenia, peripheral neuropathy, and epistaxis.', 'Metabolism', 'Caution is advised when palbociclib is used with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1473683, 'Golimumab', 'Palbociclib', 'Major', 'The use of tumor necrosis factor (TNF) blockers with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Serious infections and sepsis, including fatalities, have been reported with the use of TNF blockers, particularly in patients on concomitant immunosuppressive therapy. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids. Concomitant use of TNF blockers with other immunosuppressants such as azathioprine or mercaptopurine may also increase the risk of a rare and often fatal cancer of white blood cells known as hepatosplenic T-Cell lymphoma (HSTCL).', 'DDInter', 1767317476, 'Patients receiving a TNF blocker alone or with other immunosuppressive or myelosuppressive agents should be monitored closely for the development of infections. TNF blocker therapy should be discontinued if a serious infection or sepsis occurs. Close monitoring for signs and symptoms of HSTCL (e.g., splenomegaly, hepatomegaly, abdominal pain, persistent fever, night sweats, weight loss) is also recommended during use of TNF blockers, particularly in combination with other immunosuppressants such as azathioprine and mercaptopurine.', 'Synergism', 'Patients receiving a TNF blocker alone or with other immunosuppressive or myelosuppressive agents should be monitored closely for the development of infections.', NULL, NULL, 'Major', 'pharmacodynamic');
