INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1473690, 'Idelalisib', 'Palbociclib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of palbociclib, which is a substrate of the isoenzyme. The risk of adverse effects such as infections, neutropenia, leukopenia, anemia, thrombocytopenia, anorexia, nausea, vomiting, diarrhea, stomatitis, alopecia, asthenia, peripheral neuropathy, and epistaxis may be increased.', 'DDInter', 1767317476, 'Concomitant use of palbociclib with potent CYP450 3A4 inhibitors should generally be avoided. Alternative agents with no or minimal CYP450 3A4 inhibitory potential are recommended whenever possible. If no alternatives exist, consider reducing the palbociclib dosage to 75 mg once daily. Following discontinuation of the potent CYP450 3A4 inhibitor, the palbociclib dosage should be returned (after 3 to 5 half-lives of the inhibitor) to that used prior to initiation of the inhibitor.', 'Metabolism', 'Concomitant use of palbociclib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
