INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (250001, 'Ibritumomab tiuxetan', 'Diclofenac (topical)', 'Moderate', 'Systemically and topically administered nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of bleeding in patients treated with anticoagulants and other drugs that affect hemostasis such as platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia.', NULL, 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis.', NULL, 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis.  Close clinical and laboratory observation for hematologic complications is recommended.  Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/296177/', '[1] "Product Information. Acular (ketorolac)." Allergan Inc  (2001):[2] "Product Information. Xibrom (bromfenac ophthalmic)." ISTA Pharmaceuticals  (2005):[3] "Product Information. Nevanac (nepafenac ophthalmic)." Alcon Laboratories Inc  (2005):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (250002, 'Diclofenac (topical)', 'Ketoconazole', 'Major', 'Use of hepatotoxic drugs together with ketoconazole or levoketoconazole may potentiate the risk of liver injury.', NULL, 'The use of ketoconazole or levoketoconazole with other potentially hepatotoxic agents should be avoided.', NULL, 'The use of ketoconazole or levoketoconazole with other potentially hepatotoxic agents should be avoided.  Patients treated with levoketoconazole or ketoconazole should have liver tests performed prior to and during treatment.  Levoketoconazole manufacturer recommends interrupting treatment immediately if signs of hepatotoxicity occur.  Refer to levoketoconazole labeling for specific instructions on management of hepatotoxicity.  Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/296178/', '[1] "Product Information. Ketoconazole (ketoconazole)." Mylan Pharmaceuticals Inc  (2019):[2] "Product Information. Recorlev (levoketoconazole)." Xeris Pharmaceuticals Inc  (2022):[3] Auchus R, Pivonello R, Fleseriu M, et al. "Levoketoconazole: a novel treatment for endogenous Cushing''s syndrome.  https://www.tandfonline.com/doi/pdf/10.1080/17446651.2021.1945440"   (2022):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (250003, 'Brentuximab vedotin', 'Levodopa', 'Moderate', 'Polyneuropathy has been reported during treatment with levodopa/carbidopa combinations, and concurrent use of other medications that are also associated with this adverse effect may potentiate the risk and/or severity of nerve damage.', NULL, 'Before initiating treatment with enteral formulations of levodopa/carbidopa, the manufacturers recommend evaluating patients for history or signs of polyneuropathy and known risk factors such as diabetes mellitus, hypothyroidism, or concomitant use of other medications associated with polyneuropathy.', NULL, 'Before initiating treatment with enteral formulations of levodopa/carbidopa, the manufacturers recommend evaluating patients for history or signs of polyneuropathy and known risk factors such as diabetes mellitus, hypothyroidism, or concomitant use of other medications associated with polyneuropathy.  For patients with preexisting polyneuropathy, the benefits of treatment should be carefully weighed against the potential risks, including the potential for impaired mobility.  Plasma concentrations of vitamin B12, vitamin B6, homocysteine, methylmalonic acid, and folic acid should be obtained at baseline and at regular intervals during treatment.  Patients who develop symptoms of peripheral neuropathy and low plasma concentrations of vitamin B6 and/or vitamin B12, or elevated homocysteine or methylmalonic acid concentrations, may benefit from vitamin supplementation.  Physicians should carefully evaluate if a dose adjustment is warranted and assess the benefit versus risk of continued treatment.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/296179/', '[1] "Product Information. Duopa (carbidopa-levodopa)." AbbVie US LLC  (2022):[2] "Product Information. Duodopa (carbidopa-levodopa)." AbbVie Corporation  (2022):[3] "Product Information. Duodopa (carbidopa-levodopa)." AbbVie Pty Ltd  (2021):', 1767365211);
