INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (250008, 'Amisulpride', 'Levonordefrin', 'Moderate', 'Phenothiazines and other neuroleptics may inhibit or reverse the pressor effect of adrenaline (epinephrine), dopamine, and similar vasoconstrictors.  Many of these agents, including the atypical antipsychotics, exhibit alpha-1 adrenergic blocking activity and produce hypotension as an adverse effect.  Use of adrenaline or dopamine for drug-induced hypotension and circulatory collapse in patients receiving neuroleptic therapy may cause a paradoxical further lowering of blood pressure, since beta stimulation may worsen hypotension in the setting of alpha blockade.', NULL, 'Adrenaline, dopamine, and similar vasoconstrictors should not be used to treat drug-induced hypotension and circulatory collapse in patients taking phenothiazines or other neuroleptic agents.', NULL, 'Adrenaline, dopamine, and similar vasoconstrictors should not be used to treat drug-induced hypotension and circulatory collapse in patients taking phenothiazines or other neuroleptic agents.  Alternative vasopressor agents such as metaraminol, noradrenaline (norepinephrine), or phenylephrine should be considered, and vital signs closely monitored.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/296187/', '[1] Foster CA, O''Mullane EJ, Gaskell P, Churchill-Davidson HC "Chlorpromazine: a study of its action on the circulation in man." Lancet 2 (1954):  614-7[2] Ginsburg J, Duff RS "Effect of chlorpromazine on adrenaline vasoconstriction in man." Br J Pharmacol 11 (1956):  180-5[3] "Product Information. Thorazine (chlorpromazine)." SmithKline Beecham  (2002):[4] "Product