INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (250052, 'Linezolid', 'Levonordefrin', 'Major', 'Linezolid may potentiate the pressor response to sympathomimetic agents.  Linezolid is a reversible, nonselective monoamine oxidase inhibitor (MAOI) and, as such, may enhance sympathomimetic effect by increasing norepinephrine storage in adrenergic neurons.  The interaction may be more likely to occur with indirect- or mixed-acting sympathomimetics such as pseudoephedrine or ephedrine than with direct-acting agents like epinephrine, norepinephrine, and isoproterenol.', NULL, 'Unless blood pressure and clinical status can be closely monitored, linezolid should not be administered in combination with direct- or indirect-acting sympathomimetic agents (e.', NULL, 'Unless blood pressure and clinical status can be closely monitored, linezolid should not be administered in combination with direct- or indirect-acting sympathomimetic agents (e.g., pseudoephedrine, phenylpropanolamine), vasopressive agents (e.g., epinephrine, norepinephrine), or dopaminergic agents (e.g., dopamine, dobutamine).  If the combination is used, lower initial dosages of adrenergic agents such as dopamine or epinephrine are recommended, with careful titration to the desired response.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/296235/', '[1] "Product Information. Zyvox (linezolid)." Pharmacia and Upjohn  (2001):[2] Hendershot PE, Antal EJ, Welshman IR, Batts DH, Hopkins NK "Linezolid: pharmacokinetic and pharmacodynamic evaluation of coadministration with pseudoephedrine HCl, phenylpropanolamine HCl, and dextromethorpan HBr." J Clin Pharmacol 41 (2001):  563-72', 1767365