INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (250067, 'Midodrine', 'Levonordefrin', 'Moderate', 'The pressor effects of midodrine may be potentiated by the concomitant use of other agents that cause vasoconstriction, including sympathomimetics and ergot alkaloids.  Midodrine is an alpha-adrenergic agonist and may cause marked elevation of supine arterial blood pressure, or supine hypertension.', NULL, 'Caution is advised if midodrine must be used in combination with other agents that can increase blood pressure.', NULL, 'Caution is advised if midodrine must be used in combination with other agents that can increase blood pressure.  Supine and sitting blood pressures should be closely monitored.  Supine hypertension can often be controlled by keeping the patient from being fully supine, for example, sleeping with the head of the bed elevated.  Taking the last daily dose of midodrine 3 to 4 hours before bedtime may also help to minimize nighttime supine hypertension.  Patients should be advised to contact their physician immediately if they experience symptoms of supine hypertension such as cardiac awareness, pounding in the ears, headache, and blurred vision.  Midodrine should be discontinued if supine hypertension persists.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/296252/', '[1] "Product Information. ProAmatine (midodrine)." Roberts Pharmaceutical Corporation  (2001):[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365211);
