INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (250336, 'Epirubicin', 'Picosulfuric acid', 'Moderate', 'The use of bowel cleansing preparations may increase the risk of ventricular arrhythmia, particularly torsade de pointes, in patients treated with drugs that prolong the QT interval.', NULL, 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with drugs that prolong the QT interval.', NULL, 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with drugs that prolong the QT interval.  Monitoring of baseline and posttreatment serum electrolyte levels is recommended, particularly in the elderly.  Patients should be instructed to drink plenty of clear liquids before, during, and after the bowel preparation process.  Consideration should be given to consumption of 36 to 48 fluid ounces of a carbohydrate-electrolyte solution in the six hours before the first dose.  Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/296549/', '[1] Hill AG, Parry BR "Hypokalaemia following bowel cleansing with sodium phosphate." N Z Med J 109 (1996):  347[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Fleet Phospho Soda (sodium acid phophate-sodium phosphate)." Fleet, CB  (2007):[4] "Product Information. Visicol (sodium acid phophate-sodium phosphate)." Salix Pharmaceuticals  (2007):[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] "Product Information. Suprep Bowel Prep Kit (magnesium/potassium/sodium sulfates)." Braintree Laboratories  (2010):[7] "Product Information. Suclear (polyethylene glycol 3350 with electrolytes)." Braintree Laboratories  (2013):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (250337, 'Eprosartan', 'Picosulfuric acid', 'Moderate', 'Coadministration with agents that affect renal function or perfusion such as diuretics, ACE inhibitors, angiotensin receptor blockers, and nonsteroidal anti-inflammatory drugs (NSAIDs) may increase the risk and/or severity of fluid and electrolyte disturbances associated with the use of bowel cleansing preparations.', NULL, 'Caution is advised when bowel cleansing preparations are used in patients treated with agents that affect renal function or perfusion, particularly if they are frail or elderly or have preexisting renal impairment.', NULL, 'Caution is advised when bowel cleansing preparations are used in patients treated with agents that affect renal function or perfusion, particularly if they are frail or elderly or have preexisting renal impairment.  Baseline and postprocedure labs including serum electrolytes, BUN, and creatinine should be considered.  Patients should be advised to drink sufficient quantities of clear fluids before, during, and after bowel cleansing.  Hospitalization and intravenous fluid hydration may be appropriate for frail or elderly patients who may be unable to drink an adequate volume of fluid.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/296550/', '[1] "Product Information. Suprep Bowel Prep Kit (magnesium/potassium/sodium sulfates)." Braintree Laboratories  (2010):[2] "Product Information. Suclear (polyethylene glycol 3350 with electrolytes)." Braintree Laboratories  (2013):', 1767365211);
