INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (250464, 'Picosulfuric acid', 'Milnacipran', 'Moderate', 'The risk of seizures induced by the use of bowel cleansing preparations may be increased in patients on concomitant medications that can lower the seizure threshold such as selective serotonin reuptake inhibitors (SSRI antidepressants or anorectics), monoamine oxidase inhibitors, neuroleptic agents, central nervous system stimulants, opioids, tricyclic antidepressants, other tricyclic compounds (e.g., cyclobenzaprine, phenothiazines), systemic steroids, carbapenems, cholinergic agents, fluoroquinolones, interferons, chloroquine, mefloquine, lindane, and theophylline.', NULL, 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with agents that can lower the seizure threshold.', NULL, 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with agents that can lower the seizure threshold.  Bowel cleansing preparations should not be used if these patients have impaired renal function or perfusion, dehydration, or uncorrected electrolyte abnormalities.  Baseline and postprocedure labs including serum electrolytes, calcium, phosphate, BUN, and creatinine should be considered, particularly in the elderly.  Patients should be advised not to exceed the recommended dosage of their bowel cleansing preparation and to drink sufficient quantities of clear fluids before, during, and after the bowel preparation process.  Administration of an electrolyte rehydration solution may help attenuate the electrolyte abnormalities and hypovolemia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/296688/', '[1] Salik JM, Kurtin P "Severe hyponatremia after colonoscopy preparation in a patient with the acquired immune deficiency syndrome." Am J Gastroenterol 80 (1985):  177-9[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Fleet Phospho Soda (sodium acid phophate-sodium phosphate)." Fleet, CB  (2007):[4] "Product Information. Visicol (sodium acid phophate-sodium phosphate)." Salix Pharmaceuticals  (2007):[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] "Product Information. Suprep Bowel Prep Kit (magnesium/potassium/sodium sulfates)." Braintree Laboratories  (2010):[7] "Product Information. Suclear (polyethylene glycol 3350 with electrolytes)." Braintree Laboratories  (2013):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (250465, 'Picosulfuric acid', 'Minocycline', 'Moderate', 'Theoretically, use of sodium picosulfate during or following antibiotic treatment may result in a reduced laxative effect of sodium picosulfate.  The proposed mechanism involves antibiotic-induced reduction of colonic bacteria that hydrolyze sodium picosulfate, a prodrug, to its active metabolite bis-(p-hydroxyphenyl)-pyridyl-2-methane (BHPM), a compound that stimulates colonic peristalsis.', NULL, 'Consider use of an alternate laxative in patients who have recently taken or are currently taking an antibiotic.', NULL, 'Consider use of an alternate laxative in patients who have recently taken or are currently taking an antibiotic.  If concomitant use is required, additional bowel cleansing and colonoscopy may be required.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/296689/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Prepopik (citric acid/Mg oxide/Na picosulfate)." Ferring Pharmaceuticals Inc  (2022):[3] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):[4] "Product Information. Clenpiq (citric acid/Mg oxide/Na picosulfate)." Ferring Pharmaceuticals Inc  (2022):[5] "MHRA. Medicines & Healthcare products Regulatory Agency (general site Reference)  http://www.mhra.gov.uk/spc-pil/index.htm"   (2018):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (250466, 'Picosulfuric acid', 'Mirtazapine', 'Moderate', 'The risk of seizures induced by the use of bowel cleansing preparations may be increased in patients on concomitant medications that can lower the seizure threshold, including psychotherapeutic agents (antidepressants; neuroleptics; lithium), fluoroquinolones, some antimalarial agents (chloroquine; hydroxychloroquine; mefloquine), and some narcotic analgesics.', NULL, 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with agents that can lower the seizure threshold and/or prolong the QT interval.', NULL, 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with agents that can lower the seizure threshold and/or prolong the QT interval.  Bowel cleansing preparations should not be used if these patients have impaired renal function or perfusion, dehydration, or uncorrected electrolyte abnormalities.  Baseline and postprocedure labs including serum electrolytes, phosphate, BUN, and creatinine should be considered, particularly in the elderly.  Patients should be advised not to exceed the recommended dosage of their bowel cleansing preparation and to drink sufficient quantities of clear fluids before, during, and after the bowel preparation process.  Administration of an electrolyte rehydration solution may help attenuate the electrolyte abnormalities and hypovolemia.  Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/296690/', '[1] Hill AG, Parry BR "Hypokalaemia following bowel cleansing with sodium phosphate." N Z Med J 109 (1996):  347[2] Salik JM, Kurtin P "Severe hyponatremia after colonoscopy preparation in a patient with the acquired immune deficiency syndrome." Am J Gastroenterol 80 (1985):  177-9[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Canadian Pharmacists Association "e-CPS.  http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink"   (2006):[5] "Product Information. Fleet Phospho Soda (sodium acid phophate-sodium phosphate)." Fleet, CB  (2007):[6] "Product Information. Visicol (sodium acid phophate-sodium phosphate)." Salix Pharmaceuticals  (2007):[7] Cerner Multum, Inc. "Australian Product Information." O 0[8] "Product Information. Suprep Bowel Prep Kit (magnesium/potassium/sodium sulfates)." Braintree Laboratories  (2010):[9] "Product Information. Suclear (polyethylene glycol 3350 with electrolytes)." Braintree Laboratories  (2013):', 1767365211);
