INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (250540, 'Plazomicin', 'Picosulfuric acid', 'Moderate', 'Theoretically, use of sodium picosulfate during or following antibiotic treatment may result in a reduced laxative effect of sodium picosulfate.  The proposed mechanism involves antibiotic-induced reduction of colonic bacteria that hydrolyze sodium picosulfate, a prodrug, to its active metabolite bis-(p-hydroxyphenyl)-pyridyl-2-methane (BHPM), a compound that stimulates colonic peristalsis.', NULL, 'Consider use of an alternate laxative in patients who have recently taken or are currently taking an antibiotic.', NULL, 'Consider use of an alternate laxative in patients who have recently taken or are currently taking an antibiotic.  If concomitant use is required, additional bowel cleansing and colonoscopy may be required.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/296766/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Prepopik (citric acid/Mg oxide/Na picosulfate)." Ferring Pharmaceuticals Inc  (2022):[3] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):[4] "Product Information. Clenpiq (citric acid/Mg oxide/Na picosulfate)." Ferring Pharmaceuticals Inc  (2022):[5] "MHRA. Medicines & Healthcare products Regulatory Agency (general site Reference)  http://www.mhra.gov.uk/spc-pil/index.htm"   (2018):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (250541, 'Polyethylene glycol (3350)', 'Picosulfuric acid', 'Moderate', 'Concomitant use of stimulant laxatives (e.g., bisacodyl, sodium picosulfate) may increase the risk of serious gastrointestinal adverse effects associated with certain osmotic laxatives (e.g., polyethylene glycol (PEG), oral sulfate solution), such as colonic mucosal ulcerations or ischemic colitis.', NULL, 'The manufacturers for some osmotic bowel cleansing products recommend avoiding the concurrent use of stimulant laxatives.', NULL, 'The manufacturers for some osmotic bowel cleansing products recommend avoiding the concurrent use of stimulant laxatives.  However, stimulant laxatives, in particular bisacodyl and sodium picosulfate, are sometimes used with PEG in certain bowel cleansing regimens to help reduce dose volume and improve patient tolerability and acceptance.  Please consult individual product labeling for specific recommendations and guidance.  Patients using osmotic bowel cleansing products and stimulant laxatives who present with sudden abdominal pain, rectal bleeding, or other symptoms of ischemic colitis should be evaluated promptly.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/296768/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Baudet JS, Castro V, Redondo I "Recurrent ischemic colitis induced by colonoscopy bowel lavage." Am J Gastroenterol 105 (2010):  700-1[4] "Product Information. Suprep Bowel Prep Kit (magnesium/potassium/sodium sulfates)." Braintree Laboratories  (2010):[5] Ajani S, Hurt RT, Teeters