INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (250738, 'Bisacodyl', 'Sodium sulfate', 'Moderate', 'Concomitant use of stimulant laxatives (e.g., bisacodyl, sodium picosulfate) may increase the risk of serious gastrointestinal adverse effects associated with certain osmotic laxatives (e.g., polyethylene glycol (PEG), oral sulfate solution), such as colonic mucosal ulcerations or ischemic colitis.', NULL, 'The manufacturers for some osmotic bowel cleansing products recommend avoiding the concurrent use of stimulant laxatives.', NULL, 'The manufacturers for some osmotic bowel cleansing products recommend avoiding the concurrent use of stimulant laxatives.  However, stimulant laxatives, in particular bisacodyl and sodium picosulfate, are sometimes used with PEG in certain bowel cleansing regimens to help reduce dose volume and improve patient tolerability and acceptance.  Please consult individual product labeling for specific recommendations and guidance.  Patients using osmotic bowel cleansing products and stimulant laxatives who present with sudden abdominal pain, rectal bleeding, or other symptoms of ischemic colitis should be evaluated promptly.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/296996/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Baudet JS, Castro V, Redondo I "Recurrent ischemic colitis induced by colonoscopy bowel lavage." Am J Gastroenterol 105 (2010):  700-1[4] "Product Information. Suprep Bowel Prep Kit (magnesium/potassium/sodium sulfates)." Braintree Laboratories  (2010):[5] Ajani S, Hurt RT, Teeters DA, Bellmore LR "Ischaemic colitis associated with oral contraceptive and bisacodyl use." BMJ Case Rep 2012 (2012):[6] "Product Information. MoviPrep (polyethylene glycol 3350 with electrolytes)." Physicians Total Care  (2016):[7] "Product Information. Plenvu (polyethylene glycol 3350 with electrolytes)." Bausch Health US (formerly Valeant Pharmaceuticals)  (2020):[8] "Product Information. GaviLyte-H and Bisacodyl with Flavor Packs (bisacodyl-PEG 3350 with electrolytes)." Gavis Pharmaceuticals  (2022):[9] "Product Information. Bi-Peglyte (bisacodyl-PEG 3350 with electrolytes)." Pendopharm[10] Vaizman K, Li J, Iswara K, Tenner S "Ischemic colitis induced by the combination of Bisacodyl and polyethylene glycol in preparation for colonoscopy." Am J Gastroenterol 102 (2007):  S267[11] Belsey J, Epstein O, heresbach D "Systematic review: adverse event reports for oral sodium phosphate and polyethylene glycol." Aliment Pharmacol Ther 29 (2009):  15-28[12] Hung SY, Chen HC, Chen WT "A randomized trial comparing the bowel cleansing efficacy of sodium picosulfate/magnesium citrate and polyethylene glycol/Bisacodyl (The Bowklean Study)" Sci Rep 10 (2020):  5604[13] Adamcewicz M, Bearelly D, Porat G, Friedenberg FK "Mechanism of action and toxicities of purgatives used for colonoscopy preparation." Expert Opin Drug Metab Toxicol 7 (2011):  89-101[14] Anastassopoulos K, Farraye FA, Knight T, Colman S, Cleveland MvB, Pelham RW "A comparative study of treatment-emergent adverse events following use of common bowel preparations among a colonoscopy screening population: results from a post-marketing observational study." Dig Dis Sci 61 (2016):  2993-3006[15] Barbeau P, Wolfe D, Yazdi F, et al. "Comparative safety of bowel cleansers: protocol for a systematic review and network meta-analysis." BMJ Open 8 (2018):  e021892', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (250739, 'Blinatumomab', 'Sodium sulfate', 'Moderate', 'The risk of seizures induced by the use of bowel cleansing preparations may be increased in patients on concomitant medications that can lower the seizure threshold such as selective serotonin reuptake inhibitors (SSRI antidepressants or anorectics), monoamine oxidase inhibitors, neuroleptic agents, central nervous system stimulants, opioids, tricyclic antidepressants, other tricyclic compounds (e.g., cyclobenzaprine, phenothiazines), systemic steroids, carbapenems, cholinergic agents, fluoroquinolones, interferons, chloroquine, mefloquine, lindane, and theophylline.', NULL, 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with agents that can lower the seizure threshold.', NULL, 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with agents that can lower the seizure threshold.  Bowel cleansing preparations should not be used if these patients have impaired renal function or perfusion, dehydration, or uncorrected electrolyte abnormalities.  Baseline and postprocedure labs including serum electrolytes, calcium, phosphate, BUN, and creatinine should be considered, particularly in the elderly.  Patients should be advised not to exceed the recommended dosage of their bowel cleansing preparation and to drink sufficient quantities of clear fluids before, during, and after the bowel preparation process.  Administration of an electrolyte rehydration solution may help attenuate the electrolyte abnormalities and hypovolemia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/