INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251067, 'Methyltestosterone', 'Ketoconazole', 'Major', 'Use of hepatotoxic drugs together with ketoconazole or levoketoconazole may potentiate the risk of liver injury.', NULL, 'The use of ketoconazole or levoketoconazole with other potentially hepatotoxic agents should be avoided.', NULL, 'The use of ketoconazole or levoketoconazole with other potentially hepatotoxic agents should be avoided.  Patients treated with levoketoconazole or ketoconazole should have liver tests performed prior to and during treatment.  Levoketoconazole manufacturer recommends interrupting treatment immediately if signs of hepatotoxicity occur.  Refer to levoketoconazole labeling for specific instructions on management of hepatotoxicity.  Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297345/', '[1] "Product Information. Ketoconazole (ketoconazole)." Mylan Pharmaceuticals Inc  (2019):[2] "Product Information. Recorlev (levoketoconazole)." Xeris Pharmaceuticals Inc  (2022):[3] Auchus R, Pivonello R, Fleseriu M, et al. "Levoketoconazole: a novel treatment for endogenous Cushing''s syndrome.  https://www.tandfonline.com/doi/pdf/10.1080/17446651.2021.1945440"   (2022):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251068, 'Esterified estrogens', 'Somapacitan', 'Moderate', 'Coadministration of oral estrogen replacement therapy may diminish the therapeutic effects of human growth hormone.  Some of the pharmacodynamic effects of human growth hormone are primarily mediated by insulin-like growth factor 1 (IGF-1), and orally administered estrogen can suppress growth hormone (GH)-dependent IGF-1 production in the liver.', NULL, 'Patients receiving human growth hormone with oral estrogen replacement therapy should be monitored for potentially diminished GH efficacy.', NULL, 'Patients receiving human growth hormone with oral estrogen replacement therapy should be monitored for potentially diminished GH efficacy.  A higher dosage of growth hormone may be required to maintain serum IGF-1 levels within the normal age-appropriate range in order to achieve the defined treatment goal.  Please consult individual product labeling for the appropriate initial dosage of growth hormone recommended.  Dosage adjustments as well as clinical and laboratory monitoring should be considered following the addition or discontinuation of oral estrogen replacement therapy during use of growth hormone.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297347/', '[1] "Product Information. Humatrope (somatropin)." Lilly, Eli and Company  (2003):[2] "Product Information. Zorbtive (somatropin)." Serono Laboratories Inc  (2004):[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Cook DM, Ludlam WH, Cook MB "Route of estrogen administration helps to determine growth hormone (GH) replacement dose in GH-deficient adults." J Clin Endocrinol Metab 84 (1999):  3956-60', 1767363028);
