INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251071, 'Phenylbutazone', 'Norgestrel', 'Moderate', 'Limited clinical data suggest that phenylbutazone may reduce the efficacy of contraceptive hormones, potentially resulting in menstrual abnormalities (e.g., breakthrough bleeding, amenorrhea, irregular menses) or unintended pregnancy.  The proposed mechanism is accelerated clearance of the hormones due to induction of hepatic CYP450 enzymes by phenylbutazone.', NULL, 'Women using low-dose hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with phenylbutazone.', NULL, 'Women using low-dose hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with phenylbutazone.  Alternative or additional methods of birth control should be used during and probably for at least one week after short-term and up to one month after long-term (greater than 4 weeks) phenylbutazone therapy.  Intrauterine systems are unlikely to be significantly affected because of their local action.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297353/', '[1] Kleier DJ, Tucker JE "Oral contraceptive failure secondary to dentally prescribed drugs: fact or fiction?" J Colo Dent Assoc 66 (1987):  5-6[2] "Product Information. NuvaRing (ethinyl estradiol-etonogestrel)." Organon  (2001):[3] "Product Information. Ortho Evra (ethinyl estradiol-norelgestromin)." Janssen Pharmaceuticals  (2001):[4] Faculty of Sexual & Reproductive Healthcare "FSRH Clinical Guidance: Drug Interactions with Hormonal Contraception.  file:///C:/Users/df033684/Downloads/ceuguidancedruginteractionshormonal.pdf"   (2016):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251072, 'Somapacitan', 'Norgestrel', 'Moderate', 'Coadministration with human growth hormone may decrease the plasma concentrations of drugs that are metabolized by CYP450 3A4.  Data from an interaction study performed in growth hormone deficient adults suggest that growth hormone (somatropin) administration can increase the clearance of compounds known to be metabolized by cytochrome P450 isoenzymes, especially CYP450 3A4.', NULL, 'Caution is advised when human growth hormone is used concurrently with drugs that are known CYP450 3A4 substrates, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when human growth hormone is used concurrently with drugs that are known CYP450 3A4 substrates, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever human growth hormone is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297354/', '[1] "Product Information. Humatrope (somatropin)." Lilly, Eli and Company  (2003):[2] "Product Information. Zorbtive (somatropin)." Serono Laboratories Inc  (2004):[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Cerner Multum, Inc. "Australian Product Information." O 0', 1767363028);
