INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251126, 'Dalfopristin', 'Cilostazol', 'Major', 'Coadministration with inhibitors of CYP450 3A4 and/or 2C19 may increase the plasma concentrations of cilostazol and or its pharmacologically active metabolites, which are substrates of these isoenzymes.', NULL, 'A 50% dosage reduction of cilostazol (i.', NULL, 'A 50% dosage reduction of cilostazol (i.e., 50 mg twice a day) should be considered when used with potent or moderate CYP450 3A4 and/or 2C19 inhibitors.  Close clinical and laboratory monitoring is advised whenever the inhibitor is added to or withdrawn from therapy, and the cilostazol dosage adjusted as necessary.  Patients should be advised to contact their physician if they experience adverse effects of cilostazol such as dizziness, nausea, diarrhea, bleeding, or irregular heartbeat.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297449/', '[1] "Product Information. Pletal (cilostazol)." Otsuka American Pharmaceuticals Inc  (2001):[2] Suri A, Bramer SL "Effect of omeprazole on the metabolism of cilostazol." Clin Pharmacokinet 37 (1999):  53-9[3] Suri A, Forbes WP, Bramer SL "Effects of CYP3A inhibition on the metabolism of cilostazol." Clin Pharmacokinet 37 (1999):  61-8[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251127, 'Dalfopristin', 'Cisapride', 'Major', 'Based on in vitro inhibition data, coadministration with dalfopristin-quinupristin may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme, including certain agents that can cause prolongation of the QTc interval (e.g., cisapride, disopyramide, pimozide, quinidine, tacrolimus).', NULL, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of agents that may prolong the QTc interval, concomitant use with dalfopristin-quinupristin should be avoided if they are known to be substrates of CYP450 3A4.', NULL, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of agents that may prolong the QTc interval, concomitant use with dalfopristin-quinupristin should be avoided if they are known to be substrates of CYP450 3A4.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297450/', '[1] "Product Information. Synercid (dalfopristin-quinupristin)." Rhone Poulenc Rorer  (2001):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251128, 'Dalfopristin', 'Citalopram', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 is not expected to significantly affect the pharmacokinetics of citalopram or escitalopram, both of which are partially metabolized by the isoenzyme.', NULL, 'Coadministration with inhibitors of CYP450 3A4 is not expected to significantly affect the pharmacokinetics of citalopram or escitalopram, both of which are partially metabolized by the isoenzyme.', NULL, 'Coadministration with inhibitors of CYP450 3A4 is not expected to significantly affect the pharmacokinetics of citalopram or escitalopram, both of which are partially metabolized by the isoenzyme.  Administration of racemic citalopram (40 mg) in combination with the potent CYP450 3A4 inhibitor ketoconazole (200 mg) decreased the ketoconazole peak plasma concentration (Cmax) by 21% and systemic exposure (AUC) by 10%, but did not significantly affect the pharmacokinetics of citalopram.  Likewise, coadministration of a single 20 mg dose of escitalopram with a single 600 mg dose of ritonavir, another potent CYP450 3A4 inhibitor, did not affect the pharmacokinetics of either drug.  Because citalopram and escitalopram are both metabolized by multiple isoenzymes (CYP450 2C19, 3A4, and 2D6), inhibition of a single isoenzyme may not appreciably decrease their clearance.', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297451/', '[1] "Product Information. Celexa (citalopram)." Forest Pharmaceuticals  (2001):[2] "Product Information. Lexapro (escitalopram)." Forest Pharmaceuticals  (2002):', 1767363028);
