INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251146, 'Dasatinib', 'Dalfopristin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations and pharmacologic effects of dasatinib, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised if dasatinib is used in combination with moderate CYP450 3A4 inhibitors.', NULL, 'Caution is advised if dasatinib is used in combination with moderate CYP450 3A4 inhibitors.  Close monitoring for toxicity (e.g., myelosuppression, bleeding complications, fluid retention) is recommended, and the dasatinib dosage adjusted as necessary.  Patients should also be monitored for altered efficacy and safety of the concomitant administered drug.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297471/', '[1] "Product Information. Sprycel (dasatinib)." Bristol-Myers Squibb  (2006):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251147, 'Deflazacort', 'Dalfopristin', 'Major', 'Coadministration with potent or moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of 21-desdeflazacort, the active metabolite of deflazacort that is formed by esterases after oral administration and further metabolized by CYP450 3A4 to several inactive metabolites.', NULL, 'The manufacturer suggests using one-third of the recommended dosage of deflazacort when coadministered with potent or moderate CYP450 3A4 inhibitors.', NULL, 'The manufacturer suggests using one-third of the recommended dosage of deflazacort when coadministered with potent or moderate CYP450 3A4 inhibitors.  Some authorities recommend avoiding concomitant use of deflazacort and CYP450 3A inhibitors unless the benefit outweighs the risk, in which case patients should be monitored for systemic corticosteroid side effects.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297472/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Emflaza (deflazacort)." Marathon Pharmaceuticals  (2017):', 1767363028);
