INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251214, 'Isavuconazonium', 'Dalfopristin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of isavuconazole, which is primarily metabolized by CYP450 3A4 and 3A5 and subsequently by uridine diphosphate glucuronosyltransferases (UGT).', NULL, 'Caution is advised when isavuconazonium sulfate is prescribed with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when isavuconazonium sulfate is prescribed with CYP450 3A4 inhibitors.  Patients should be monitored for adverse effects such as nausea, vomiting, diarrhea, peripheral edema, hypokalemia, hypomagnesemia, and hepatotoxicity.  In addition, many CYP450 3A4 inhibitors are also substrates of the isoenzyme, thus pharmacologic response to these agents should also be monitored, as isavuconazole itself is reportedly a moderate CYP450 3A4 inhibitor.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297545/', '[1] "Product Information. Cresemba (isavuconazonium)." Astellas Pharma US, Inc  (2015):', 1767363028);
