INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251216, 'Istradefylline', 'Dalfopristin', 'Moderate', 'Coadministration with moderate inhibitors of CYP450 3A4 may increase the plasma concentrations of istradefylline, which is primarily metabolized by CYP450 3A4 and 1A1.', NULL, 'Caution is advised if istradefylline is used with moderate CYP450 3A4 inhibitors.', NULL, 'Caution is advised if istradefylline is used with moderate CYP450 3A4 inhibitors.  It is unknown if istradefylline will require a dosage adjustment if coadministered with a moderate CYP450 3A4 inhibitor.  Clinical and laboratory monitoring of istradefylline should be considered whenever a moderate CYP450 3A4 inhibitor is added to or withdrawn from therapy.  Patients should be monitored for adverse effects including dyskinesia, impulse control issues, dizziness, constipation, nausea, hallucinations, and insomnia.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297547/', '[1] "Product Information. Nourianz (istradefylline)." Kyowa Kirin, Inc  (2019):', 1767363028);
