INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251235, 'Dalfopristin', 'Lorlatinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of lorlatinib, which has been found to be metabolized primarily by CYP450 3A4 and UGT1A4 in vitro.', NULL, 'Caution is advised when lorlatinib is used with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when lorlatinib is used with CYP450 3A4 inhibitors.  Patients should be monitored for the development of central nervous system and other adverse effects, and the dosing adjusted or discontinued in accordance with the lorlatinib product labeling.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297568/', '[1] "Product Information. Lorbrena (lorlatinib)." Pfizer U.S. Pharmaceuticals Group  (2018):', 1767363028);
