INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251238, 'Dalfopristin', 'Lumateperone', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of lumateperone.', NULL, 'The recommended dosage of lumateperone is 21 mg once daily when coadministered with moderate CYP450 3A4 inhibitors.', NULL, 'The recommended dosage of lumateperone is 21 mg once daily when coadministered with moderate CYP450 3A4 inhibitors.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297571/', '[1] "Product Information. Caplyta (lumateperone)." Intra-Cellular Therapies, Inc.  (2022):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251239, 'Dalfopristin', 'Lurasidone', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of lurasidone and its active metabolite, both of which are primarily metabolized by the isoenzyme.', NULL, 'Lurasidone dosage should start at 20 mg/day and not exceed 80 mg/day when coadministered with moderate CYP450 3A4 inhibitors.', NULL, 'Lurasidone dosage should start at 20 mg/day and not exceed 80 mg/day when coadministered with moderate CYP450 3A4 inhibitors.  If a moderate CYP450 3A4 inhibitor is added to existing lurasidone therapy, the lurasidone dosage should be reduced by half.  Concomitant use of lurasidone and ritonavir- or cobicistat-boosted atazanavir is considered to be contraindicated.  Patients should be monitored for potentially increased adverse effects such as dizziness, drowsiness, dry mouth, increased appetite, weight gain, extrapyramidal symptoms, tardive dyskinesia, hyperglycemia, dyslipidemia, hyperprolactinemia (galactorrhea, amenorrhea, gynecomastia), orthostatic hypotension, cognitive and motor impairment, seizures, dysphagia, and heat-related illnesses due to disruption of body temperature regulation.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297572/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Latuda (lurasidone)." Sunovion Pharmaceuticals Inc  (2010):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251240, 'Dalfopristin', 'Lurbinectedin', 'Major', 'Coadministration with a moderate CYP450 3A4 inhibitor is predicted to increase lurbinectedin systemic exposure, which may increase the incidence and severity of adverse reactions to lurbinectedin, such as myelosuppression and hepatotoxicity.', NULL, 'Greater attention should be paid to myelosuppression and hepatotoxicit in combination with lurbinectedi and moderate CYP450 3A4 inhibitors, and the effects of this interaction should be considered if the patient develops an unusual condition.', NULL, 'Greater attention should be paid to myelosuppression and hepatotoxicit in combination with lurbinectedi and moderate CYP450 3A4 inhibitors, and the effects of this interaction should be considered if the patient develops an unusual condition.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297573/', '[1] "Product Information. Zepzelca (lurbinectedin)." Jazz Pharmaceuticals  (2020):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251241, 'Dalfopristin', 'Macitentan', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 or moderate dual or combined inhibitors of CYP450 3A4 and CYP450 2C9 may increase the plasma concentrations of macitentan.  Macitentan is primarily metabolized by CYP450 3A4 and to a minor extent by CYP450 2C8, CYP450 2C9 and CYP450 2C19.', NULL, 'Caution is advisable if macitentan is used with inhibitors of CYP450 3A4.', NULL, 'Caution is advisable if macitentan is used with inhibitors of CYP450 3A4.  The product labeling recommends avoiding concomitant use with potent inhibitors (e.g., protease inhibitors, clarithromycin, cobicistat, conivaptan, delavirdine, itraconazole, ketoconazole, nefazodone, posaconazole, voriconazole).  The manufacturer of macitentan also recommends avoiding concomitant use with moderate dual inhibitors of CYP450 3A4 and 2C9 (e.g., fluconazole, amiodarone) or in combination with both a moderate CYP450 3A4 inhibitor and a moderate CYP450 2C9 inhibitor.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297574/', '[1] "Product Information. Opsumit (macitentan)." Actelion Pharmaceuticals US Inc  (2013):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251242, 'Dalfopristin', 'Maraviroc', 'Moderate', 'Coadministration with inhibitors of the CYP450 3A4 isoenzyme and/or P-glycoprotein efflux transporter may increase the plasma concentrations of maraviroc, which is a substrate of both.', NULL, 'Caution is advised when maraviroc is used with inhibitors of CYP450 3A4 and/or P-glycoprotein.', NULL, 'Caution is advised when maraviroc is used with inhibitors of CYP450 3A4 and/or P-glycoprotein.  Dosage adjustment for maraviroc may be necessary if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297575/', '[1] "Product Information. Selzentry (maraviroc)." Pfizer U.S. Pharmaceuticals Group  (2007):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251243, 'Dalfopristin', 'Medroxyprogesterone acetate', 'Moderate', 'Based on in vitro inhibition data, coadministration with dalfopristin-quinupristin may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme.  The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by dalfopristin-quinupristin.', NULL, 'Caution is advised if dalfopristin-quinupristin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if dalfopristin-quinupristin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever dalfopristin-quinupristin is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297577/', '[1] "Product Information. Synercid (dalfopristin-quinupristin)." Rhone Poulenc Rorer  (2001):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251244, 'Dalfopristin', 'Methadone', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of methadone, which is metabolized primarily by CYP450 3A4, 2B6, 2C19, and to a lesser extent by CYP450 2C9 and 2D6.', NULL, 'Caution is advised if methadone is prescribed in combination with CYP450 3A4 inhibitors.', NULL, 'Caution is advised if methadone is prescribed in combination with CYP450 3A4 inhibitors.  Pharmacologic response to methadone should be closely monitored and the dosage adjusted accordingly, particularly following initiation or discontinuation of the CYP450 3A4 inhibitor in patients who are stabilized on their methadone regimen.  Patients should be advised to report excessive drowsiness, nausea, or asthenia to their physician, and to seek immediate medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.  ECG monitoring should be considered for patients on methadone with heart or liver disease; conduction abnormalities; electrolyte disturbances (i.e., hypokalemia, hypomagnesemia); concomitant use of drugs that may cause QT prolongation or electrolyte loss; concomitant use of CYP450 3A4 inhibitors; or use of methadone at dosages greater than 200 mg daily.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297578/', '[1] Bell J, Seres V, Bowron P, Lewis J, Batey R "The use of serum methadone levels in patients receiving methadone maintenance." Clin Pharmacol Ther 43 (1988):  62