INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251270, 'Dalfopristin', 'Olaparib', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of olaparib, which is primarily metabolized by the isoenzyme.', NULL, 'MANAGEMENT Concomitant use of olaparib with moderate CYP450 3A4 inhibitors should be avoided whenever possible.', NULL, 'MANAGEMENT Concomitant use of olaparib with moderate CYP450 3A4 inhibitors should be avoided whenever possible.  If coadministration is required, dose reductions are recommended as follows:  the olaparib tablet dosage should be reduced to 150 mg twice a day; the olaparib capsule dosage should be reduced to 200 mg twice a day.  Specific dose recommendations for each formulation should be followed as the tablet and capsule formulations are not interchangeable due to differences in bioavailability.  Once the CYP450 3A4 inhibitor has been discontinued for 3 to 5 elimination half-lives, the usual olaparib dose should be resumed.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297607/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Lynparza (olaparib)." Astra-Zeneca Pharmaceuticals  (2014):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251271, 'Dalfopristin', 'Oliceridine', 'Major', 'Coadministration with moderate or potent inhibitors of CYP450 2D6 and/or 3A4 may increase the plasma concentrations and adverse opioid effects of oliceridine, which is primarily metabolized by both isoenzymes in vitro.  In addition, oliceridine concentrations may decrease following discontinuation of a concomitant moderate or potent CYP450 2D6 or 3A4 inhibitor, possibly resulting in decreased opioid efficacy or withdrawal syndrome.', NULL, 'Caution is recommended if oliceridine is used in combination with moderate or potent inhibitors of CYP450 2D6 or 3A4.', NULL, 'Caution is recommended if oliceridine is used in combination with moderate or potent inhibitors of CYP450 2D6 or 3A4.  If concomitant use is considered necessary, patients may require less frequent dosing of oliceridine and should be closely monitored for respiratory depression, sedation, and QT prolongation.  If a moderate or potent CYP450 2D6 or 3A4 inhibitor is discontinued, an increase of the oliceridine dosage may be considered while monitoring for signs of opioid withdrawal until stable drug effects are achieved.  Due to the prolonged duration of CYP450 2D6 inhibition by the moderate CYP450 2D6 inhibitor rolapitant, prolonged monitoring for adverse effects of oliceridine for at least 28 days after administration of rolapitant may be required.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297608/', '[1] "Product Information. Varubi (rolapitant)." Tesaro Inc.  (2015):[2] "Product Information. Olinvyk (oliceridine)." Trevena Inc  (2020):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251272, 'Dalfopristin', 'Osilodrostat', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of osilodrostat, which is partially metabolized by the isoenzyme.', NULL, 'Caution is advised when osilodrostat is coadministered with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when osilodrostat is coadministered with CYP450 3A4 inhibitors.  Dosage adjustments may be required based on clinical response and tolerance.  Patients should have regular monitoring of 24-hour urine free cortisol and serum or plasma cortisol during treatment, as well as regular evaluations for signs and symptoms of hypocortisolism such as nausea, vomiting, abdominal pain, loss of appetite, fatigue, dizziness, hypotension, abnormal electrolyte levels, and hypoglycemia.  Decrease dosing or temporarily discontinue osilodrostat if patients experience symptoms of hypocortisolism or if urine free cortisol levels fall below the target range or there is a rapid decrease in cortisol levels.  Stop osilodrostat and administer exogenous glucocorticoid replacement therapy if patients have symptoms of adrenal insufficiency and serum or plasma cortisol levels are below target range.  Osilodrostat therapy may be restarted at a lower dosage when symptoms have resolved and cortisol values are within target range.  Additionally, an electrocardiogram and serum electrolyte levels should also be obtained prior to initiating osilodrostat, with ECG repeated within one week after starting treatment and periodically thereafter.  Correct hypokalemia and/or hypomagnesemia before starting treatment and as indicated during treatment, as they may be risk factors for ventricular arrhythmias.  If QTc interval exceeds 480 msec at any point, temporary dose reduction, interruption, or discontinuation of osilodrostat may be necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297610/', '[1] "Product Information. Isturisa (osilodrostat)." Recordati Rare Diseases Inc  (2020):[2] "Product Information. Isturisa (osilodrostat)." Recordati Rare Diseases Australia Pty Ltd  (2022):[3] "Product Information. Isturisa (osilodrostat)." Recordati Rare Diseases UK Ltd  (2021):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251273, 'Dalfopristin', 'Osimertinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of osimertinib, which has been shown in vitro to be primarily metabolized by the isoenzyme.', NULL, 'Caution is advised when osimertinib is prescribed with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when osimertinib is prescribed with CYP450 3A4 inhibitors.  Patients should be monitored for adverse effects such as diarrhea, QT prolongation, torsade de pointes arrhythmia, and cardiomyopathy (e.g., cardiac failure, pulmonary edema, ejection fraction decreases), and the osimertinib dosage adjusted accordingly or treatment discontinued as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297611/', '[1] "Product Information. Tagrisso (osimertinib)." Astra-Zeneca Pharmaceuticals  (2015):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251274, 'Dalfopristin', 'Ospemifene', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of ospemifene, which is partially metabolized by the isoenzyme.', NULL, 'Caution is advised when ospemifene is used with potent CYP450 3A4 inhibitors.', NULL, 'Caution is advised when ospemifene is used with potent CYP450 3A4 inhibitors.  Patients should be advised to seek medical attention if they develop potential signs and symptoms of thromboembolism such as chest pain, shortness of breath, and pain or swelling in the arms or legs.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297612/', '[1] "Product Information. Osphena (ospemifene)." Shionogi USA Inc  (2013):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251275, 'Dalfopristin', 'Oxycodone', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of oxycodone, which is substantially metabolized by the isoenzyme.  Increased oxycodone concentrations could conceivably increase or prolong adverse drug effects and may cause potentially fatal respiratory depression.', NULL, 'Extreme caution is advised if oxycodone is prescribed with CYP450 3A4 inhibitors, particularly potent and moderate inhibitors (e.', NULL, 'Extreme caution is advised if oxycodone is prescribed with CYP450 3A4 inhibitors, particularly potent and moderate inhibitors (e.g., azole antifungal agents, protease inhibitors, aprepitant, ciprofloxacin, chloramphenicol, clarithromycin, cobicistat, conivaptan, crizotinib, delavirdine, diltiazem, dronedarone, erythromycin, fusidic acid, idelalisib, imatinib, mibefradil, mifepristone, nefazodone, netupitant, quinupristin-dalfopristin, telithromycin, verapamil) or weak inhibitors that also inhibit CYP450 2D6 (e.g., abiraterone, amiodarone, cimetidine, pazopanib, ranolazine).  Some authorities advise that the oxycodone dose may need to be adjusted.  A fatal overdose may occur following the initiation of a CYP450 3A4 inhibitor in patients already receiving oxycodone.  Patients should be closely monitored for signs and symptoms of sedation, respiratory depression, and hypotension.  Following discontinuation of the CYP450 3A4 inhibitor, patients should be monitored for reduced efficacy of oxycodone or development of withdrawal symptoms due to reduced plasma oxycodone levels.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297613/', '[1] "Product Information. OxyContin (oxycodone)." Purdue Frederick Company  (2001):[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251276, 'Dalfopristin', 'Palbociclib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of palbociclib, which is a substrate of the isoenzyme.', NULL, 'Caution is advised when palbociclib is used with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when palbociclib is used with CYP450 3A4 inhibitors.  Dosage adjustment for palbociclib is not generally considered necessary for suspected interactions with mild to moderate inhibitors.  However, patients should be monitored for increased adverse effects such as infections, neutropenia, leucopenia, anemia, thrombocytopenia, anorexia, nausea, vomiting, diarrhea, stomatitis, alopecia, asthenia, peripheral neuropathy, and epistaxis.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297617/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Ibrance (palbociclib)." Pfizer U.S. Pharmaceuticals Group  (2015):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251277, 'Dalfopristin', 'Panobinostat', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of panobinostat, which is partially metabolized by the isoenzyme.', NULL, 'Caution is advised when panobinostat is prescribed with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when panobinostat is prescribed with CYP450 3A4 inhibitors.  Patients should be monitored for adverse effects such as nausea, vomiting, diarrhea, anorexia, peripheral edema, cardiotoxicity, ECG abnormalities, electrolyte disturbances, bleeding complications, hepatotoxicity and myelosuppression, and the dosage of panobinostat adjusted as necessary in accordance with the product labeling.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297618/', '[1] Hamberg P, Woo MM, Chen LC, et al. "Effect of ketoconazole-mediated CYP3A4 inhibition on clinical pharmacokinetics of panobinostat (LBH589), an orally active histone deacetylase inhibitor." Cancer Chemother Pharmacol 68 (2011):  805-13[2] "Product Information. Farydak (panobinostat)." Novartis Pharmaceuticals  (2015):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251278, 'Dalfopristin', 'Pazopanib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of pazopanib, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised if pazopanib is prescribed in combination with inhibitors of CYP450 3A4.', NULL, 'Caution is advised if pazopanib is prescribed in combination with inhibitors of CYP450 3A4.  Pharmacologic response to pazopanib should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the pazopanib dosage adjusted as necessary.  Patients should have liver function tests (ALT, AST, bilirubin), electrocardiograms, and serum electrolyte levels performed at baseline and regular intervals as recommended in the product labeling.  Patients should be advised to notify their physician if they experience signs and symptoms of hepatotoxicity such as fever, rash, anorexia, nausea, vomiting, fatigue, right upper quadrant pain, dark urine, and jaundice.  In addition, they should seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297619/', '[1] "Product Information. Votrient (pazopanib)." GlaxoSmithKline  (2009):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251279, 'Dalfopristin', 'Pemigatinib', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may increase the plasma concentrations of pemigatinib, which is primarily metabolized by the isoenzyme in vitro.', NULL, 'Concomitant use of pemigatinib with moderate CYP450 3A4 inhibitors should be avoided.', NULL, 'Concomitant use of pemigatinib with moderate CYP450 3A4 inhibitors should be avoided.  If coadministration is required, a reduction in the dosage of pemigatinib is recommended.  Patients receiving 13.5 mg of pemigatinib should have the dosage reduced to 9 mg, and those receiving 9 mg should have the dosage reduced to 4.5 mg.  After discontinuation of the moderate CYP450 3A4 inhibitor for 3 plasma half-lives, the pemigatinib dosage that was taken prior to initiating the inhibitor may be resumed.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297620/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Pemazyre (pemigatinib)." Incyte Corporation  (2020):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251280, 'Dalfopristin', 'Pexidartinib', 'Major', 'Coadministration of pexidartinib with moderate CYP450 3A4 inhibitors may significantly increase the plasma concentrations and the incidence and severity of adverse effects of pexidartinib, including potentially fatal hepatotoxicity.  The proposed mechanism is inhibition of CYP450 3A4, the primary isoenzyme responsible for the metabolic clearance of pexidartinib.', NULL, 'The use of pexidartinib with a moderate CYP450 3A4 inhibitor should generally be avoided.', NULL, 'The use of pexidartinib with a moderate CYP450 3A4 inhibitor should generally be avoided.  If concomitant use is required, the dose of pexidartinib should be reduced according to the manufacturer''s recommendations.  If concomitant use of a moderate CYP450 3A4 inhibitor is discontinued, the dose of pexidartinib may be increased, after 3 plasma half-lives of the moderate CYP450 3A4 inhibitor, to the dose that was used prior to starting the moderate CYP450 3A4 inhibitor.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297621/', '[1] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc.  (2019):', 1767363028);
