INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251296, 'Dalfopristin', 'Ranolazine', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of ranolazine, which is primarily metabolized by the isoenzyme.  Because ranolazine prolongs QT interval in a dose-dependent manner, high plasma levels of ranolazine may increase the risk of ventricular arrhythmias such as ventricular tachycardia, ventricular fibrillation, and torsade de pointes.', NULL, 'The dosage of ranolazine should not exceed 500 mg twice a day when coadministered with moderate inhibitors of CYP450 3A4, including but not limited to diltiazem, verapamil, aprepitant, erythromycin, fluconazole, and grapefruit juice.', NULL, 'The dosage of ranolazine should not exceed 500 mg twice a day when coadministered with moderate inhibitors of CYP450 3A4, including but not limited to diltiazem, verapamil, aprepitant, erythromycin, fluconazole, and grapefruit juice.  Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297638/', '[1] "Product Information. Ranexa (ranolazine)." Calmoseptine Inc  (2006):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251297, 'Dalfopristin', 'Repaglinide', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of repaglinide, which is partially metabolized by the isoenzyme.', NULL, 'Because the antidiabetic effect of repaglinide is dose- and concentration-dependent, pharmacologic response to repaglinide should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy.', NULL, 'Because the antidiabetic effect of repaglinide is dose- and concentration-dependent, pharmacologic response to repaglinide should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy.  Patients should be advised to regularly monitor their blood sugar and counseled on how to recognize and treat hypoglycemia, which may include symptoms such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, and palpitations.  The repaglinide dosage may require adjustment if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297640/', '[1] "Product Information. Prandin (repaglinide)." Novo Nordisk Pharmaceuticals Inc  (2001):[2] Niemi M, Neuvonen PJ, Kivisto KT "The cytochrome P4503A4 inhibitor clarithromycin increases the plasma concentrations and effects of repaglinide." Clin Pharmacol Ther 70 (2001):  58-65[3] Niemi M, Backman JT, Neuvonen M, Neuvonen PJ "Effects of gemfibrozil, itraconazole, and their combination on the pharmacokinetics and pharmacodynamics of repaglinide: potentially hazardous interaction between gemfibrozil and repaglinide." D