INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251309, 'Dalfopristin', 'Saxagliptin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may alter the plasma concentrations of saxagliptin and its pharmacologically active metabolite, both of which are substrates of the isoenzyme.', NULL, 'Pharmacologic response to saxagliptin should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy.', NULL, 'Pharmacologic response to saxagliptin should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy.  Patients should be advised to regularly monitor their blood sugar and counseled on how to recognize and treat hypoglycemia, which may include symptoms such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, and palpitations.  The physician should be notified if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297653/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Onglyza (saxagliptin)." Bristol-Myers Squibb  (2009):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251310, 'Dalfopristin', 'Selpercatinib', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of selpercatinib, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of selpercatinib with moderate CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of selpercatinib with moderate CYP450 3A4 inhibitors should generally be avoided.  If coadministration is required, the manufacturer recommends decreasing the dosage of selpercatinib.  Patients receiving selpercatinib 120 mg twice daily should have the dosage reduced to 80 mg twice daily, and those receiving 160 mg twice daily should have the dosage reduced to 120 mg twice daily.  Close monitoring for adverse effects is advisable, including more frequent ECGs and laboratory monitoring of liver enzymes, bilirubin, electrolytes, glucose, and blood counts.  Selpercatinib treatment should be discontinued, interrupted, or dosage reduced in patients with serious or life-threatening toxicities in accordance with the product labeling.  After the inhibitor has been discontinued for 3 to 5 elimination half-lives, the selpercatinib dosage that was taken prior to initiating the inhibitor may be resumed.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297654/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Retevmo (selpercatinib)." Lilly, Eli and Company  (2020):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251311, 'Dalfopristin', 'Selumetinib', 'Major', 'Coadministration with potent or moderate inhibitors of CYP450 3A4 may increase the plasma concentrations of selumetinib, which is primarily metabolized by CYP450 3A4 and to a lesser extent by CYP450 2C19, 1A2, 2C9, 2E1, and 3A5.  Selumetinib also undergoes glucuronidation by UGT1A1 and UGT1A3.', NULL, 'Concomitant use of selumetinib with potent or moderate CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of selumetinib with potent or moderate CYP450 3A4 inhibitors should generally be avoided.  If coadministration is required, a reduction in the dosage of selumetinib is recommended.  Patients receiving selumetinib 25 mg/m2 twice daily should have the dosage reduced to 20 mg/m2 twice daily, and those receiving 20 mg/m2 twice daily should have the dosage reduced to 15 mg/m2 twice daily.  Further dosage adjustments should be made according to clinical response and tolerance.  Please refer to the product labeling for more detailed information on dosing adjustments.  After discontinuation of the strong or moderate CYP450 3A4 inhibitor for 3 elimination half-lives, the selumetinib dosage that was taken prior to initiating the inhibitor may be resumed.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297655/', '[1] "Product Information. Koselugo (selumetinib)." Astra-Zeneca Pharmaceuticals  (2020):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251312, 'Dalfopristin', 'Sertraline', 'Moderate', 'Based on in vitro inhibition data, coadministration with dalfopristin-quinupristin may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme.  The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by dalfopristin-quinupristin.', NULL, 'Caution is advised if dalfopristin-quinupristin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if dalfopristin-quinupristin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever dalfopristin-quinupristin is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297656/', '[1] "Product Information. Synercid (dalfopristin-quinupristin)." Rhone Poulenc Rorer  (2001):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251313, 'Dalfopristin', 'Sibutramine', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of sibutramine and its active metabolites, which are metabolized by the isoenzyme.', NULL, 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of sibutramine and its active metabolites, which are metabolized by the isoenzyme.', NULL, 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of sibutramine and its active metabolites, which are metabolized by the isoenzyme.  Inhibitors such as ketoconazole, cimetidine, and erythromycin have caused moderate increases in the peak plasma concentration (Cmax) and area under the concentration-time curve (AUC) of the active metabolites.  The data suggest that although the potential for such interaction exists, the magnitude appears to be small.  No special precaution appears necessary; however, dosage adjustments as well as clinical and laboratory monitoring may be appropriate in some cases when a CYP450 3A4 inhibitor is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297657/', '[1] "Product Information. Meridia (sibutramine)." Knoll Pharmaceutical Company  (2001):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251314, 'Dalfopristin', 'Sildenafil', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of sildenafil, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised if sildenafil is coadministered with CYP450 3A4 inhibitors.', NULL, 'Caution is advised if sildenafil is coadministered with CYP450 3A4 inhibitors.  Dosage adjustments may be appropriate for sildenafil whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy based on efficacy and side effects.  Patients should be advised to promptly notify their physician if they experience pain or tightness in the chest or jaw, irregular heartbeat, nausea, shortness of breath, visual disturbances, syncope, or prolonged erection (greater than 4 hours).', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297658/', '[1] "Product Information. Viagra (sildenafil)." Pfizer U.S. Pharmaceuticals  (2001):[2] Warrington JS, Shader RI, vonMoltke LL, Greenblatt DJ "In vitro biotransformation of sildenafil (Viagra): Identification of human cytochromes and potential drug interactions." Drug Metab Disposition 28 (2000):  392-7[3] Hyland R, Roe GH, Jones BC, Smith DA "Identification of the cytochrome P450 enzymes involved in the N-demethylation of sildenafil." Br J Clin Pharmaacol 51 (2001):  239-48[4] "Product Information. Revatio (sildenafil)." Pfizer U.S. Pharmaceuticals Group  (2005):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251315, 'Dalfopristin', 'Silodosin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of silodosin, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised if silodosin is prescribed in combination with inhibitors of CYP450 3A4.', NULL, 'Caution is advised if silodosin is prescribed in combination with inhibitors of CYP450 3A4.  Pharmacologic response to silodosin should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the silodosin dosage adjusted as necessary.  Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.  Taking silodosin at bedtime may minimize the occurrence of orthostatic effects.  Patients should also avoid driving or operating hazardous machinery until they know how the medication affects them.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297659/', '[1] "Product Information. Rapaflo (silodosin)." Watson Pharmaceuticals  (2008):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251316, 'Dalfopristin', 'Simvastatin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of HMG-CoA reductase inhibitors (i.e., statins) that are metabolized by the isoenzyme.  Lovastatin and simvastatin are particularly susceptible because of their low oral bioavailability, but others such as atorvastatin and cerivastatin may also be affected.  High levels of HMG-CoA reductase inhibitory activity in plasma is associated with an increased risk of musculoskeletal toxicity.  Myopathy manifested as muscle pain and/or weakness associated with grossly elevated creatine kinase exceeding ten times the upper limit of normal has been reported occasionally.  Rhabdomyolysis has also occurred rarely, which may be accompanied by acute renal failure secondary to myoglobinuria and may result in death.  Clinically significant interactions have been reported with potent CYP450 3A4 inhibitors such as macrolide antibiotics, azole antifungals, protease inhibitors and nefazodone, and moderate inhibitors such as amiodarone, cyclosporine, danazol, diltiazem and verapamil.', NULL, 'Caution is recommended if atorvastatin, cerivastatin, lovastatin, simvastatin, or red yeast rice (which contains lovastatin) is prescribed with a CYP450 3A4 inhibitor.', NULL, 'Caution is recommended if atorvastatin, cerivastatin, lovastatin, simvastatin, or red yeast rice (which contains lovastatin) is prescribed with a CYP450 3A4 inhibitor.  It is advisable to monitor lipid levels and use the lowest effective statin dose.  All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine.  Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.  Fluvastatin, pravastatin, and rosuvastatin are not expected to interact with CYP450 3A4 inhibitors.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297660/', '[1] Spach DH, Bauwens JE, Clark CD, Burke WG "Rhabdomyolysis associated with lovastatin and erythromycin use." West J Med 154 (1991):  213-5[2] Ayanian JZ, Fuchs CS, Stone RM "Lovastatin and rhabdomyolysis." Ann Intern Med 109 (1988):  682-3[3] Corpier CL, Jones PH, Suki WN, et al. "Rhabdomyolysis and renal injury with lovastatin use. Report of two cases in cardiac transplant recipients." JAMA 260 (1988):  239-41[4] East C, Alivizatos PA, Grundy SM, Jones PH, Farmer JA "Rhabdomyolysis in patients receiving lovastatin after cardiac transplantation." N Engl J Med 318 (1988):  47-8[5] Norman DJ, Illingworth DR, Munson J, Hosenpud J "Myolysis and acute renal failure in a heart-transplant recipient receiving lovastatin." N Engl J Med 318 (1988):  46-7[6] Ayanian JZ, Fuchs CS, Stone RM "Lovastatin and rhabdomyolysis." Ann Intern Med 109 (1988):  682-3[7] "Product Information. Mevacor (lovastatin)." Merck & Co., Inc  (2002):[8] "Product Information. Zocor (simvastatin)." Merck & Co., Inc  (2001):[9] Dallaire M, Chamberland M "Severe rhabdomyolysis in a patient receiving lovastatin, danazol and doxycycline." Can Med Assoc J 150 (1994):  1991-4[10] Campana C, Iacona I, Regassi MB, et al. "Efficacy and pharmacokinetics of simvastatin in heart transplant recipients." Ann Pharmacother 29 (1995):  235-9[11] Lees RS, Lees AM "Rhabdomyolysis from the coadministration of lovastatin and the antifungal agent itraconazole." N Engl J Med 333 (1995):  664-5[12] Zhou LX, Finley DK, Hassell AE, Holtzman JL "Pharmacokinetic interactio