INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251317, 'Dalfopristin', 'Siponimod', 'Major', 'Coadministration with drugs that cause moderate inhibition of CYP450 2C9 and moderate or strong inhibition of CYP450 3A4 may increase the plasma concentrations of siponimod, which is primarily metabolized by these isoenzymes.  This interaction includes concomitant use of siponimod with a moderate CYP450 2C9/3A4 dual inhibitor or a moderate CYP450 2C9 inhibitor in combination with a separate moderate or strong CYP450 3A4 inhibitor.', NULL, 'Concomitant use of siponimod with a moderate or strong CYP450 2C9/3A4 dual inhibitor (e.', NULL, 'Concomitant use of siponimod with a moderate or strong CYP450 2C9/3A4 dual inhibitor (e.g., fluconazole, mifepristone) or a moderate CYP450 2C9 inhibitor (e.g., abiraterone, amiodarone, fenofibrate, gemfibrozil, nitisinone, oxandrolone) in combination with a moderate or strong CYP450 3A4 inhibitor (e.g., amprenavir, aprepitant, atazanavir, boceprevir, ceritinib, chloramphenicol, ciprofloxacin, clarithromycin, cobicistat, conivaptan, crizotinib, dalfopristin-quinupristin, darunavir, delavirdine, diltiazem, dronedarone, erythromycin, fedratinib, fluvoxamine, fosamprenavir, fosaprepitant, fosnetupitant, fusidic acid, idelalisib, imatinib, indinavir, isavuconazole, itraconazole, ketoconazole, letermovir, mibefradil, nefazodone, nelfinavir, netupitant, posaconazole, ribociclib, ritonavir, saquinavir, stiripentol, telaprevir, telithromycin, troleandomycin, verapamil, voriconazole, voxelotor) is not recommended.  Caution is advised if siponimod is used in combination with moderate CYP450 2C9 inhibitors.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297661/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Mayzent (siponimod)." Novartis Pharmaceuticals  (2019):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251318, 'Dalfopristin', 'Sirolimus', 'Moderate', 'Coadministration with drugs that are inhibitors of CYP450 3A4 may increase the blood concentrations of the macrolide immunosuppressants sirolimus and tacrolimus, both of which are metabolized by the isoenzyme.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of macrolide immunosuppressant therapy should be considered, including adverse effects such as fever, infection, diarrhea, hypokalemia, anemia, thrombocytopenia, leukopenia, and hyperlipidemia.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of macrolide immunosuppressant therapy should be considered, including adverse effects such as fever, infection, diarrhea, hypokalemia, anemia, thrombocytopenia, leukopenia, and hyperlipidemia.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297662/', '[1] "Product Information. Prograf (tacrolimus)." Fujisawa  (2001):[2] "Product Information. Parlodel (bromocriptine)." Sandoz Pharmaceuticals Corporation  (2001):[3] Christians U, Schmidt G, Bader A, et al. "Identification of drugs inhibiting the in vitro metabolism of tacrolimus by human liver microsomes." Br J Clin Pharmacol 41 (1996):  187-90[4] "Product Information. Rapamune (sirolimus)." Wyeth-Ayerst Laboratories  (2001):[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Cerner Multum, Inc. "Australian Product Information." O 0[7] "Product Information. Qelbree (viloxazine)." Supernus Pharmaceuticals Inc  (2021):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251319, 'Dalfopristin', 'Solifenacin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of solifenacin, which is primarily metabolized by the isoenzyme.', NULL, 'Pharmacologic response to solifenacin should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the solifenacin dosage adjusted if necessary.', NULL, 'Pharmacologic response to solifenacin should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the solifenacin dosage adjusted if necessary.  Patients should be advised to contact their physician if they experience undue adverse effects of solifenacin such as severe abdominal pain or constipation for 3 or more days.  Patients should seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitations, irregular heartbeat, shortness of breath, or syncope.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297665/', '[1] "Product Information. VESIcare (solifenacin)." GlaxoSmithKline  (2004):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251320, 'Dalfopristin', 'Sonidegib', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of sonidegib, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of sonidegib with moderate CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of sonidegib with moderate CYP450 3A4 inhibitors should generally be avoided.  If coadministration is required, the manufacturer recommends limiting use of the moderate CYP450 3A4 inhibitor to less than 14 days and monitoring for adverse reactions, particularly musculoskeletal toxicity.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297666/', '[1] "Product Information. Odomzo (sonidegib)." Novartis Pharmaceuticals  (2015):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251321, 'Dalfopristin', 'Stiripentol', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2, 2C19, and/or 3A4 may increase the plasma concentrations of stiripentol, which has been shown in vitro to undergo phase I metabolism via these pathways.', NULL, 'Caution is advised when stiripentol is used with CYP450 1A2, 2C19, and/or 3A4 inhibitors.', NULL, 'Caution is advised when stiripentol is used with CYP450 1A2, 2C19, and/or 3A4 inhibitors.  Patients should be monitored for potentially increased adverse effects such as anorexia, nausea, vomiting, weight loss, somnolence, dizziness, confusion, difficulty concentrating, ataxia, hypotonia, tremor, hyperkinesia, dysarthria, suicidal ideation/behaviour, neutropenia, and thrombocytopenia.  Dosage adjustments for stiripentol or alternative treatments may be required if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297668/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports.  http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid"   (2007):[3] "Product Information. Diacomit (stiripentol)." Biocodex USA  (2018):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251322, 'Dalfopristin', 'Sufentanil', 'Moderate', 'Based on in vitro inhibition data, coadministration with dalfopristin-quinupristin may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme.  The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by dalfopristin-quinupristin.', NULL, 'Caution is advised if dalfopristin-quinupristin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if dalfopristin-quinupristin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever dalfopristin-quinupristin is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297669/', '[1] "Product Information. Synercid (dalfopristin-quinupristin)." Rhone Poulenc Rorer  (2001):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251323, 'Dalfopristin', 'Suvorexant', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of suvorexant, which is primarily metabolized by the isoenzyme.', NULL, 'The recommended dose of suvorexant is 5 mg when used with moderate CYP450 3A4 inhibitors, and generally should not exceed 10 mg.', NULL, 'The recommended dose of suvorexant is 5 mg when used with moderate CYP450 3A4 inhibitors, and generally should not exceed 10 mg.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297670/', '[1] "Product Information. Belsomra (suvorexant)." Merck & Company Inc, Whitehouse Station, NJ.[2] "Product Information. Belsomra (suvorexant)." Merck & Company Inc, Whitehouse Station, NJ.', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251324, 'Dalfopristin', 'Tacrolimus', 'Major', 'Based on in vitro inhibition data, coadministration with dalfopristin-quinupristin may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme, including certain agents that can cause prolongation of the QTc interval (e.g., cisapride, disopyramide, pimozide, quinidine, tacrolimus).', NULL, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of agents that may prolong the QTc interval, concomitant use with dalfopristin-quinupristin should be avoided if they are known to be substrates of CYP450 3A4.', NULL, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of agents that may prolong the QTc interval, concomitant use with dalfopristin-quinupristin should be avoided if they are known to be substrates of CYP450 3A4.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297671/', '[1] "Product Information. Synercid (dalfopristin-quinupristin)." Rhone Poulenc Rorer  (2001):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251325, 'Dalfopristin', 'Tadalafil', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of tadalafil, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised if tadalafil is prescribed with CYP450 3A4 inhibitors.', NULL, 'Caution is advised if tadalafil is prescribed with CYP450 3A4 inhibitors.  Dosage adjustments may be appropriate for tadalafil whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy based on efficacy and side effects.  Tadalafil labeling recommends that the dosage not exceed 10 mg once every 72 hours in patients treated concomitantly with a potent CYP450 3A4 inhibitor, such as erythromycin, itraconazole, ketoconazole, protease inhibitors, and nefazodone.  Patients should be advised to promptly notify their physician if they experience potential symptoms of PDE5 inhibitor toxicity such as pain or tightness in the chest or jaw, irregular heartbeat, nausea, shortness of breath, visual disturbances, syncope, or prolonged erection (greater than 4 hours).', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297672/', '[1] "Product Information. Cialis (tadalafil)." Lilly, Eli and Company  (2003):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251326, 'Dalfopristin', 'Tamoxifen', 'Major', 'Based on in vitro inhibition data, coadministration with dalfopristin-quinupristin may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme, including certain agents that can cause prolongation of the QTc interval (e.g., cisapride, disopyramide, pimozide, quinidine, tacrolimus).', NULL, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of agents that may prolong the QTc interval, concomitant use with dalfopristin-quinupristin should be avoided if they are known to be substrates of CYP450 3A4.', NULL, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of agents that may prolong the QTc interval, concomitant use with dalfopristin-quinupristin should be avoided if they are known to be substrates of CYP450 3A4.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297673/', '[1] "Product Information. Synercid (dalfopristin-quinupristin)." Rhone Poulenc Rorer  (2001):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251327, 'Dalfopristin', 'Tamsulosin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or 2D6 may increase the plasma concentrations of tamsulosin, which is primarily metabolized in the liver by these isoenzymes.', NULL, 'Caution is advised if tamsulosin is used concomitantly with moderate CYP450 3A4 inhibitors (e.', NULL, 'Caution is advised if tamsulosin is used concomitantly with moderate CYP450 3A4 inhibitors (e.g., amiodarone, aprepitant, diltiazem, dronedarone, erythromycin, fluconazole, fluvoxamine, fusidic acid, imatinib, isavuconazonium, verapamil) and/or moderate to potent CYP450 2D6 inhibitors (e.g., abiraterone, bupropion, celecoxib, cinacalcet, darifenacin, dronedarone, duloxetine, fluoxetine, lorcaserin, paroxetine, propafenone, quinidine, ranolazine, rolapitant, terbinafine), particularly at a dosage higher than 0.4 mg/day.  The potential for increased risk of adverse effects such as postural hypotension, syncope, and priapism should be considered.  It should be noted that rolapitant, a moderate CYP450 2D6 inhibitor, can increase plasma concentrations and the risk of adverse effects of tamsulosin for at least 28 days after administration of rolapitant.  Patients should be advised to avoid rising abruptly from a sitting or recumbent position, and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.  Patients should also avoid driving or operating hazardous machinery until they know how the medication affects them.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297674/', '[1] "Product Information. Flomax (tamsulosin)." Boehringer-Ingelheim  (2001):[2] Franco-Salinas G, de la Rosette JJ, Michel MC "Pharmacokinetics and pharmacodynamics of tamsulosin in its modified-release and oral controlled absorption system formulations." Clin Pharmacokinet 49 (2010):  177-88[3] Kamimura H, Oishi S, Matsushima H, et al. "Identification of cytochrome P450 isozymes involved in metabolism of the alpha1-adrenoceptor blocker tamsulosin in human liver microsomes." Xenobiotica 28 (1998):  909-22[4] "Product Information. Varubi (rolapitant)." Tesaro Inc.  (2015):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251328, 'Dalfopristin', 'Tasimelteon', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 and/or 3A4 may increase the plasma concentrations of tasimelteon, which is primarily metabolized by these isoenzymes.', NULL, 'Caution is advised if tasimelteon is prescribed in combination with inhibitors of CYP450 1A2 and/or 3A4.', NULL, 'Caution is advised if tasimelteon is prescribed in combination with inhibitors of CYP450 1A2 and/or 3A4.  Patients should be monitored for excessive sedation and other side effects.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297675/', '[1] "Product Information. Hetlioz (tasimelteon)." Vanda Pharmaceuticals Inc  (2014):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251329, 'Dalfopristin', 'Tazemetostat', 'Major', 'Coadministration with moderate or potent CYP450 3A4 inhibitors may significantly increase the plasma concentrations of tazemetostat, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of tazemetostat with moderate or potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of tazemetostat with moderate or potent CYP450 3A4 inhibitors should generally be avoided.  Alternative therapeutic agents with less inhibitory potential should be considered whenever possible.  If coadministration with a moderate CYP450 3A4 inhibitor is required and no other alternatives are possible, the dose of tazemetostat should be reduced as per manufacturer recommendations.  After the inhibitor has been discontinued for 3 elimination half-lives, the tazemetostat dose that was taken prior to initiating the inhibitor may be resumed.  Patients should be advised to notify their health care professional if they experience signs or symptoms of possible tazemetostat-related adverse effects including secondary malignancies, hemorrhage, pleural effusion, skin infection, dyspnea, pain, and respiratory distress.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297676/', '[1] "Product Information. Tazverik (tazemetostat)." Epizyme, Inc  (2020):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251330, 'Dalfopristin', 'Temsirolimus', 'Moderate', 'Coadministration of temsirolimus with inhibitors of CYP450 3A4 may increase the plasma concentrations of sirolimus, a major active metabolite of temsirolimus and known substrate of CYP450 3A4.', NULL, 'Caution is advised if temsirolimus is prescribed in combination with CYP450 3A4 inhibitors.', NULL, 'Caution is advised if temsirolimus is prescribed in combination with CYP450 3A4 inhibitors.  Pharmacologic response to temsirolimus should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the temsirolimus dosage adjusted as necessary.  Patients should be advised to contact their physician if they experience increased adverse effects of temsirolimus such as hyperglycemia (e.g., excessive thirst; increased volume and/or frequency of urination), infections, fever, dyspnea, abdominal pain, diarrhea, and bloody stools.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297677/', '[1] "Product Information. Torisel (temsirolimus)." Wyeth-Ayerst Laboratories  (2007):', 1767363028);
