INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251360, 'Dalfopristin', 'Zileuton', 'Moderate', 'Based on in vitro inhibition data, coadministration with dalfopristin-quinupristin may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme.  The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by dalfopristin-quinupristin.', NULL, 'Caution is advised if dalfopristin-quinupristin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if dalfopristin-quinupristin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever dalfopristin-quinupristin is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297711/', '[1] "Product Information. Synercid (dalfopristin-quinupristin)." Rhone Poulenc Rorer  (2001):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251361, 'Dalfopristin', 'Ziprasidone', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may only modestly increase the plasma concentrations of ziprasidone, as less than 1/3 of ziprasidone metabolic clearance occurs via oxidation mediated by CYP450 3A4.', NULL, 'Coadministration with inhibitors of CYP450 3A4 may only modestly increase the plasma concentrations of ziprasidone, as less than 1/3 of ziprasidone metabolic clearance occurs via oxidation mediated by CYP450 3A4.', NULL, 'Coadministration with inhibitors of CYP450 3A4 may only modestly increase the plasma concentrations of ziprasidone, as less than 1/3 of ziprasidone metabolic clearance occurs via oxidation mediated by CYP450 3A4.  In 14 healthy subjects, coadministration with the potent CYP450 3A4 inhibitor ketoconazole (400 mg orally once a day for 5 days) increased the mean peak plasma concentration (Cmax) and area under the concentration-time curve (AUC) of ziprasidone (40 mg single oral dose) by approximately 1/3 compared to placebo.  These changes, although statistically significant, were not considered clinically important.  There were also no serious adverse events, laboratory or ECG abnormalities, or clinically significant alterations in vital signs throughout the study.  Likewise, in 10 healthy subjects, the nonspecific CYP450 inhibitor cimetidine (800 mg orally once a day for 3 days) increased the AUC of ziprasidone (40 mg single oral dose) by just 6% compared to when ziprasidone was administered alone.  These findings suggest that ziprasidone dose modifications are unlikely to be necessary in patients receiving potent CYP450 3A4 inhibitors.', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297712/', '[1] "Product Information. Geodon (ziprasidone)." Pfizer U.S. Pharmaceuticals  (2001):[2] Miceli JJ, Smith M, Robarge L, Morse T, Laurent A "The effects of ketoconazole on ziprasidone pharmacokinetics--a placebo-controlled crossover study in healthy volunteers." Br J Clin Pharmacol 49(suppl 1) (2000):  s71-6[3] Prakash C, Kamel A, Cui D, Whalen RD, Miceli JJ, Tweedie D "Identification of the major human liver cytochrome P450 isoform(s) responsible for the formation of the primary metabolites of ziprasidone and prediction of possible drug interactions." Br J Clin Pharmacol 49(Suppl 1) (2000):  35S-42S[4] Wilner KD, Hansen RA, Folger CJ, Geoffroy P "The pharmacokinetics of ziprasidone in healthy volunteers treated with cimetidine or antacid." Br J Clin Pharmacol 49(Suppl 1) (2000):  57S-60S', 1767363028);
