INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251391, 'Paritaprevir', 'Apalutamide', 'Moderate', 'Coadministration with apalutamide may decrease the plasma concentrations of drugs that are substrates of the metabolic enzymes CYP450 3A4, CYP450 2C19, CYP450 2C9, and uridine diphosphate glucuronosyltransferase (UGT), as well as substrates of the membrane transporters organic anion transporting polypeptide (OATP) 1B1, P-glycoprotein (P-gp), and breast cancer resistance protein (BCRP).', NULL, 'Caution is advised when apalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, CYP450 2C19, CYP450 2C9, UGT, OATP1B1, P-gp and/or BCRP, particularly sensitive substrates or those with a narrow therapeutic range.', NULL, 'Caution is advised when apalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, CYP450 2C19, CYP450 2C9, UGT, OATP1B1, P-gp and/or BCRP, particularly sensitive substrates or those with a narrow therapeutic range.  Substitution for these medications is recommended when possible, or monitor for potential loss of therapeutic efficacy if coadministration is required.  The prescribing information for concomitant medications should be consulted to assess the benefits versus risks of coadministration of a CYP450 inducer like apalutamide and for any dosage adjustments that may be required.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297760/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Erleada (apalutamide)." Janssen Biotech, Inc.  (2018):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251392, 'Paritaprevir', 'Atazanavir', 'Major', 'Coadministration of atazanavir plus ritonavir in combination with ombitasvir/paritaprevir/ritonavir plus dasabuvir may significantly increase the plasma concentrations of paritaprevir.  The mechanism may involve inhibition of both CYP450 3A4-mediated metabolism and P-glycoprotein-mediated transport by atazanavir and ritonavir.', NULL, 'When coadministered with ombitasvir/paritaprevir/ritonavir plus dasabuvir, atazanavir 300 mg should be given in the morning without ritonavir.', NULL, 'When coadministered with ombitasvir/paritaprevir/ritonavir plus dasabuvir, atazanavir 300 mg should be given in the morning without ritonavir.  The ritonavir component of the HIV antiretroviral regimen should be restarted after completion of treatment with ombitasvir/paritaprevir/ritonavir plus dasabuvir.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297764/', '[1] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprev/ritonav)." AbbVie US LLC  (2022):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251393, 'Paritaprevir', 'Atorvastatin', 'Major', 'Theoretically, coadministration with ombitasvir/paritaprevir/ritonavir with or without dasabuvir may significantly increase the plasma concentrations of certain HMG-CoA reductase inhibitors.  The proposed mechanism may involve inhibition of the hepatic uptake transporter OATP1B1 by paritaprevir and/or inhibition of CYP450 3A4 by ritonavir.', NULL, 'Concomitant use of atorvastatin and ombitasvir/paritaprevir/ritonavir with or without dasabuvir should be avoided.', NULL, 'Concomitant use of atorvastatin and ombitasvir/paritaprevir/ritonavir with or without dasabuvir should be avoided.  All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine.  Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297766/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprev/ritonav)." AbbVie US LLC  (2022):[4] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):', 1767363028);
