INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251422, 'Cyclosporine', 'Paritaprevir', 'Major', 'Coadministration with ombitasvir/paritaprevir/ritonavir plus dasabuvir may significantly increase the blood concentrations of cyclosporine.  The proposed mechanism is ritonavir inhibition of the CYP450 3A4-mediated metabolism and P-glycoprotein-mediated efflux of cyclosporine.', NULL, 'When initiating therapy with ombitasvir/paritaprevir/ritonavir plus dasabuvir, the manufacturer recommends reducing cyclosporine dosage to one-fifth of the patient''s current dosage.', NULL, 'When initiating therapy with ombitasvir/paritaprevir/ritonavir plus dasabuvir, the manufacturer recommends reducing cyclosporine dosage to one-fifth of the patient''s current dosage.  Monitoring of cyclosporine blood concentrations is advisable to determine subsequent dosage modifications, as well as frequent assessment of renal function and cyclosporine-related side effects.  Upon completion of hepatitis C therapy, the appropriate time to resume the original dosage of cyclosporine should be guided by assessment of cyclosporine blood concentrations.  Patients should be advised to notify their physician if they experience possible signs of cyclosporine toxicity such as nausea, vomiting, diarrhea, abdominal pain, dizziness, fatigue, headache, tremors, and convulsions.', 'Metabolism, Absorption', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297806/', '[1] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprev/ritonav)." AbbVie US LLC  (2022):', 1767363028);
