INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251469, 'Ethinylestradiol', 'Paritaprevir', 'Major', 'Coadministration of ethinyl estradiol-, mestranol-, or estetrol-containing products may increase the risk of liver enzyme elevations associated with the use of ombitasvir/paritaprevir/ritonavir, with or without dasabuvir.  The proposed mechanism is the inhibition of UDP-glucuronosyltransferase (UGT) by ombitasvir, paritaprevir, and dasabuvir.', NULL, 'Ethinyl estradiol-, mestranol-, or estetrol-containing medications must be discontinued prior to starting therapy with ombitasvir/paritaprevir/ritonavir, with or without dasabuvir.', NULL, 'Ethinyl estradiol-, mestranol-, or estetrol-containing medications must be discontinued prior to starting therapy with ombitasvir/paritaprevir/ritonavir, with or without dasabuvir.  Alternative contraception such as progestin-only contraceptives or non-hormonal methods are recommended.  Ethinyl estradiol-, mestranol-, or estetrol-containing medications can be restarted approximately 2 weeks following completion of the hepatitis C treatment.  Liver function tests should be performed on all patients during the first 4 weeks of treatment and as clinically indicated thereafter.  If ALT is elevated above baseline at any time during treatment, the test should be repeated and monitored closely.  Therapy should be discontinued if ALT levels remain persistently greater than 10 times the ULN, or if ALT elevations are accompanied by signs or symptoms of liver inflammation or increasing conjugated bilirubin, alkaline phosphatase, or INR.  Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297868/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprev/ritonav)." AbbVie US LLC  (2022):[4] "Product Information. Technivie (ombitasvir/paritaprevir/ritonavir)." AbbVie US LLC  (2015):[5] "Product Information. Nextstellis (drospirenone-estetrol)." Mayne Pharma  (2021):', 1767363028);
